import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{r as n,o,c as r,a as e,b as s,d as t,e as l}from"./app.b0a45b91.js";const c={},d=t("\u4E2D\u82F1\u6587\u672C\u6458\u81EA\u4E2D\u570B\u53E4\u8DE1\u907A\u5740\u4FDD\u8B77\u5354\u6703\uFF1A\u3008"),h={href:"http://icomoschina.org.cn/uploads/download/20210312144359_download.pdf",target:"_blank",rel:"noopener noreferrer"},p=t("\u6587\u5316\u907A\u7522\u4FDD\u8B77\u7BA1\u7406\u76F8\u95DC\u6587\u4EF6\u532F\u7DE8"),m=t("\u3009\uFF0C\u7B2C189-195\u9801\u3002"),u=l('<h2 id="\u95DC\u65BC\u4FDD\u8B77\u907A\u7522\u5730\u7CBE\u795E\u7684\u9B41\u5317\u514B\u5BA3\u8A00" tabindex="-1"><a class="header-anchor" href="#\u95DC\u65BC\u4FDD\u8B77\u907A\u7522\u5730\u7CBE\u795E\u7684\u9B41\u5317\u514B\u5BA3\u8A00" aria-hidden="true">#</a> \u95DC\u65BC\u4FDD\u8B77\u907A\u7522\u5730\u7CBE\u795E\u7684\u9B41\u5317\u514B\u5BA3\u8A00</h2><h3 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h3><p>2008\u5E7410\u67084\u65E5\u570B\u969B\u53E4\u8DE1\u907A\u5740\u7406\u4E8B\u6703\u7B2C16\u5C46\u5927\u6703\uFF08\u52A0\u62FF\u5927\u9B41\u5317\u514B\uFF09\u901A\u904E</p><p>2008\u5E749\u670829\u65E5\u81F310\u67084\u65E5\uFF0C\u61C9\u570B\u969B\u53E4\u8DE1\u907A\u5740\u7406\u4E8B\u6703\uFF08\u4EE5\u4E0B\u7C21\u7A31ICOMOS\uFF09\u52A0\u62FF\u5927\u570B\u5BB6\u59D4 \u54E1\u6703\u4E4B\u9080\uFF0C\u5728ICOMOS\u7B2C16\u5C46\u5927\u6703\u548C\u9B41\u5317\u514B\u5EFA\u57CE400\u5468\u5E74\u6176\u5178\u4E4B\u969B\uFF0C\u8207\u6703\u8005\u5728\u52A0\u62FF\u5927\u9019\u5EA7\u6B77\u53F2\u540D\u57CE\u901A\u904E\u4E86\u4E0B\u8FF0\u5BA3\u8A00\u3002\u8A72\u5BA3\u8A00\u901A\u904E\u4FDD\u8B77\u6709\u5F62\u548C\u7121\u5F62\u907A\u7522\uFF0C\u70BA\u4FDD\u5B58\u5730\u65B9\u7CBE\u795E\u63D0\u51FA\u5404\u9805\u539F\u5247\u548C\u5EFA\u8B70,\u88AB\u8996\u4F5C\u78BA\u4FDD\u4E16\u754C\u548C\u793E\u6703\u53EF\u6301\u7E8C\u767C\u5C55\u7684\u4E00\u500B\u5275\u65B0\u3001\u9AD8\u6548\u7684\u9014\u5F91\u3002</p><p>\u5BA3\u8A00\u662FICOMOS\u5728\u904E\u53BB5\u5E74\u9593\u70BA\u4FDD\u8B77\u548C\u63A8\u5EE3\u907A\u7522\u5730\u7CBE\u795E\uFF0C\u5373\u907A\u7522\u5730\u7684\u751F\u5B58\u3001\u793E\u6703\u548C\u7CBE\u795E\u672C\u8CEA \u6240\u63A1\u53D6\u7684\u4E00\u7CFB\u5217\u63AA\u65BD\u53CA\u884C\u52D5\u7684\u4E00\u90E8\u5206\u30022003\u5E74\uFF0CICOMOS\u5C07\u5176\u7B2C14\u5C46\u5927\u6703\u79D1\u5B78\u7814\u8A0E\u6703\u7684\u4E3B\u984C\u5B9A\u4F4D\u65BC\u4FDD\u8B77\u53E4\u8DE1\u907A\u5740\u7684\u793E\u6703\u7121\u5F62\u50F9\u503C\u3002\u5728\u96A8\u5F8C\u7684\u300A\u80AF\u4F2F\u96F7\u5BA3\u8A00\u300B\u88E1\uFF0CICOMOS\u627F\u8AFE\uFF0C\u6309\u71671972\u5E74\u300A\u4E16\u754C\u907A\u7522\u516C\u7D04\u300B\uFF0C\u91CD\u8996\u8003\u616E\u7121\u5F62\u50F9\u503C\uFF08\u8A18\u61B6\u3001\u4FE1\u4EF0\u3001\u50B3\u7D71\u77E5\u8B58\u3001\u5C0D\u907A\u7522\u5730\u7684\u60C5\u611F\uFF09\u4EE5\u53CA\u5728\u53E4\u8DE1\u907A\u5740\u7684\u7BA1\u7406\u548C\u4FDD\u8B77\u4E0A\u626E\u6F14\u524D\u8FF0\u50F9\u503C\u5B88\u8B77\u8005\u7684\u7576\u5730\u793E\u5340\u30022005\u5E74\u7684ICOMOS\u300A\u897F\u5B89\u5BA3\u8A00\u300B\u63D0\u8ACB\u6CE8\u610F\uFF0C\u5728\u4FDD\u8B77\u4E16\u754C\u53E4\u8DE1\u907A\u5740\u8207\u4FC3\u9032\u9019\u9805\u4E8B\u696D\u7684\u767C\u5C55\u6642\uFF0C\u8981\u91CD\u8996\u907A\u7522\u5730\u80CC\u666F\u74B0\u5883\u7684\u4FDD\u8B77\uFF0C\u65E2\u5305\u62EC\u5BE6\u9AD4\u3001\u8996\u89BA\u548C\u81EA\u7136\u5C64\u9762\uFF0C\u4E5F\u5305\u62EC\u793E\u6703\u548C\u4FE1\u4EF0\u5BE6\u8E10\u3001\u98A8\u4FD7\u3001\u50B3\u7D71\u77E5\u8B58\u8207\u5176\u5B83\u7121\u5F62\u7684\u65B9\u5F0F\u548C\u8868\u73FE\u5F62\u5F0F\u3002\u5BA3\u8A00\u9084\u547C\u7C72\u63A1\u53D6\u8DE8\u5B78\u79D1\u7684\u624B\u6BB5\uFF0C\u5BE6\u73FE\u8CC7\u8A0A\u4F86\u6E90\u591A\u5143\u5316\uFF0C\u4EE5\u4FBF\u66F4\u597D\u5730\u7406\u89E3\u3001\u7BA1\u7406\u548C\u4FDD\u8B77\u53E4\u8DE1\u907A\u5740\u7684\u74B0\u5883\u3002ICOMOS\u7F8E\u6D32\u5730\u5340\u59D4\u54E1\u6703\u65BC2008\u5E74\u64EC\u5B9A\u7684\u300AFoz Do Igua\xC1u\u5BA3\u8A00\u300B\u95E1\u660E\uFF0C\u907A\u7522\u7684\u6709\u5F62\u548C\u7121\u5F62\u5143\u7D20\uFF0C\u5C0D\u5DF2\u7D93\u5275\u9020\u4E26\u50B3\u627F\u5177\u6709\u6587\u5316\u548C\u6B77\u53F2\u610F\u7FA9\u7684\u907A\u7522\u5730\u793E\u5340\u7684\u7279\u6027\u7684\u4FDD\u8B77\u662F\u4E0D\u53EF\u6216\u7F3A\u7684\u3002ICOMOS\u65B0\u7684\u6709\u95DC\u6587\u5316\u7DDA\u8DEF\u548C\u907A\u7522\u95E1\u91CB\u8207\u5C55\u793A\u7684\u61B2\u7AE0\u7D93\u904E\u5EE3\u6CDB\u78CB\u5546\u5F8C\u64EC\u5B9A\uFF0C\u4E26\u5448\u5831ICOMOS\u7B2C16\u5C46\u5927\u6703\u6279\u51C6\uFF0C\u5176\u4E2D\u4E5F\u8A8D\u53EF\u4E86\u907A\u7522\u7121\u5F62\u5C64\u9762\u548C\u907A\u7522\u5730\u7CBE\u795E\u50F9\u503C\u7684\u91CD\u8981\u6027\u3002\u7531\u65BC\u6709\u5F62\u548C\u7121\u5F62\u907A\u7522\u7684\u4E0D\u53EF\u5206\u5272\u6027\uFF0C\u4EE5\u53CA\u7121\u5F62\u907A\u7522\u8CE6\u4E88\u5BE6\u7269\u548C\u907A\u7522\u5730\u7684\u610F\u7FA9\u3001\u50F9\u503C\u548C\u74B0\u5883\uFF0CICOMOS\u6B63\u8003\u616E\u901A\u904E\u91DD\u5C0D\u53E4\u8DE1\u907A\u5740\u7684\u7121\u5F62\u907A\u7522\u7279\u5225\u64EC\u5B9A\u7684\u65B0\u61B2\u7AE0\u3002\u70BA\u6B64\uFF0C\u6211\u5011\u9F13\u52F5\u901A\u904E\u958B\u5C55\u8A0E\u8AD6\u548C\u8FAF\u8AD6\uFF0C\u5236\u8A02\u51FA\u9069\u7528\u65BC\u907A\u7522\u5730\u7CBE\u795E\u672C\u9AD4\u8B8A\u5316\u7684\u65B0\u6982\u5FF5\u8A9E\u5F59\u3002</p><p>\u7B2C16\u5C46\u5927\u6703\uFF0C\u66F4\u5177\u9AD4\u8AAA\u5C31\u662F\u9752\u5E74\u8AD6\u58C7\u3001\u539F\u4F4F\u6C11\u8AD6\u58C7\u548C\u79D1\u5B78\u7814\u8A0E\u6703\uFF0C\u70BA\u9032\u4E00\u6B65\u63A2\u7D22\u6709\u5F62\u548C\u7121\u5F62\u907A\u7522\uFF0C\u4EE5\u53CA\u907A\u7522\u5730\u7CBE\u795E\u7684\u793E\u6703\u5167\u90E8\u548C\u6587\u5316\u6A5F\u5236\u9593\u7684\u95DC\u4FC2\u63D0\u4F9B\u4E86\u6A5F\u6703\u3002\u907A\u7522\u5730\u7CBE\u795E\u88AB\u754C\u5B9A\u70BA\u6709\u5F62\uFF08\u5EFA\u7BC9\u7269\u3001\u907A\u5740\u3001\u666F\u89C0\u3001\u7DDA\u8DEF\u3001\u5BE6\u7269\uFF09\u548C\u7121\u5F62\u5143\u7D20\uFF08\u8A18\u61B6\u3001\u53E3\u8FF0\u3001\u66F8\u9762\u6587\u737B\u3001\u5100\u5F0F\u3001\u6176\u5178\u6D3B\u52D5\u3001\u50B3\u7D71\u77E5\u8B58\u3001\u50F9\u503C\u3001\u8CEA\u5730\u3001\u984F\u8272\u3001\u6C23\u5473\u7B49\uFF09\uFF0C\u5373\u8CE6\u4E88\u907A\u7522\u5730\u610F\u7FA9\u3001\u50F9\u503C\u3001\u60C5\u611F\u548C\u795E\u79D8\u6027\u7684\u7269\u8CEA\u548C\u7CBE\u795E\u5143\u7D20\u3002\u6211\u5011\u6C92\u6709\u5C07\u7CBE\u795E\u8207\u907A\u7522\u5730\u3001\u6709\u5F62\u8207\u7121\u5F62\u5272\u88C2\u958B\u3001\u8996\u70BA\u5C0D\u7ACB\u7684\u4E8B\u7269\uFF0C\u800C\u662F\u63A2\u67E5\u5169\u8005\u76F8\u4E92\u5F71\u97FF\u3001\u76F8\u8F14\u76F8\u6210\u7684\u591A\u7A2E\u65B9\u5F0F\u3002\u907A\u7522\u5730\u7CBE\u795E\u7531\u5404\u7A2E\u793E\u6703\u884C\u52D5\u8005\u2014\u2014\u4E0D\u50C5\u5B83\u7684\u5EFA\u9020\u8005\u3001\u7BA1\u7406\u8005\u9084\u6709\u5B83\u7684\u4F7F\u7528\u8005\u2014\u2014\u5171\u540C\u69CB\u5EFA\u800C\u6210\uFF0C\u9019\u4E9B\u4EBA\u7A4D\u6975\u6301\u7E8C\u7684\u52AA\u529B\uFF0C\u5171\u540C\u8CE6\u4E88\u4E86\u907A\u7522\u5730\u7CBE\u795E\u4EE5\u610F\u7FA9\u3002\u88AB\u770B\u4F5C\u4E00\u7A2E\u95DC\u4FC2\u6982\u5FF5\u7684\u907A\u7522\u5730\u7CBE\u795E\u5177\u6709\u591A\u5143\u3001\u52D5\u614B\u7684\u7279\u5FB5\uFF0C\u80FD\u5920\u64C1\u6709\u591A\u91CD\u610F\u7FA9\u548C\u7279\u9EDE\uFF0C\u4E26\u96A8\u6642\u9593\u63A8\u79FB\u800C\u8B8A\u5316\uFF0C\u540C\u6642\u5C6C\u65BC\u4E0D\u540C\u7FA4\u9AD4\u3002\u9019\u7A2E\u52D5\u614B\u7684\u65B9\u6CD5\u4E5F\u66F4\u52A0\u9069\u61C9\u4ECA\u65E5\u4EE5\u8DE8\u570B\u4EBA\u53E3\u6D41\u52D5\u3001\u4EBA\u53E3\u9077\u79FB\u3001\u8DE8\u6587\u5316\u4EA4\u6D41\u3001\u591A\u5143\u793E\u6703\uFF0C\u4EE5\u53CA\u5C0D\u907A\u7522\u5730\u7684\u591A\u7A2E\u8A8D\u540C\u611F\u70BA\u7279\u5FB5\u7684\u5168\u7403\u5316\u7684\u4E16\u754C\u3002</p><p>\u907A\u7522\u5730\u7CBE\u795E\u6709\u52A9\u65BC\u5927\u5BB6\u66F4\u52A0\u5168\u9762\u5730\u77AD\u89E3\u53E4\u8DE1\u3001\u907A\u5740\u548C\u6587\u5316\u666F\u89C0\u7576\u4E0B\u7684\u548C\u6C38\u6046\u7684\u7279\u6027\u3002\u5B83\u70BA\u8A8D\u8B58\u6587\u5316\u907A\u7522\u63D0\u4F9B\u4E86\u66F4\u52A0\u8C50\u5BCC\u3001\u52D5\u614B\u7684\u5168\u65B9\u4F4D\u8996\u91CE\u3002\u907A\u7522\u5730\u7CBE\u795E\u4EE5\u4E0D\u540C\u5F62\u5F0F\u5B58\u5728\u65BC\u5168\u4E16\u754C\u6240\u6709\u7684\u6587\u5316\u4E4B\u4E2D\uFF0C\u662F\u4EBA\u985E\u70BA\u9069\u61C9\u5176\u793E\u6703\u9700\u6C42\u5275\u9020\u7684\u3002\u5728\u907A\u7522\u5730\u5167\u5C45\u4F4F\u7684\u793E\u6703\u7FA4\u9AD4\uFF0C\u7279\u5225\u662F\u50B3\u7D71\u793E\u5340\uFF0C\u61C9\u5C07\u5176\u8207\u6240\u5728\u907A\u7522\u5730\u8A18\u61B6\u3001\u751F\u547D\u529B\u3001\u5EF6\u7E8C\u6027\u548C\u7CBE\u795E\u7684\u4FDD\u8B77\u5BC6\u5207\u806F\u7E6B\u3002</p><p>\u56E0\u6B64\uFF0CICOMOS\u7B2C16\u5C46\u5927\u6703\u7684\u8207\u6703\u8005\uFF0C\u5411\u5404\u653F\u5E9C\u548C\u975E\u653F\u5E9C\u7D44\u7E54\u3001\u570B\u5BB6\u548C\u5730\u65B9\u7576\u5C40\uFF0C\u4EE5\u53CA\u6240 \u6709\u6A5F\u69CB\u548C\u5C08\u5BB6\u63D0\u51FA\u4EE5\u4E0B\u539F\u5247\u548C\u5EFA\u8B70\u7684\u5BA3\u8A00\uFF0C\u5E0C\u671B\u4ED6\u5011\u80FD\u5920\u901A\u904E\u7ACB\u6CD5\u3001\u5236\u5B9A\u653F\u7B56\u3001\u898F\u5283\u7A0B\u5F0F\u548C\u7BA1\u7406\u7B49\u624B\u6BB5\u66F4\u597D\u5730\u4FDD\u8B77\u8207\u5BA3\u50B3\u907A\u7522\u5730\u7CBE\u795E\u3002</p><h3 id="\u907A\u7522\u5730\u7CBE\u795E\u7684\u518D\u601D\u8003" tabindex="-1"><a class="header-anchor" href="#\u907A\u7522\u5730\u7CBE\u795E\u7684\u518D\u601D\u8003" aria-hidden="true">#</a> \u907A\u7522\u5730\u7CBE\u795E\u7684\u518D\u601D\u8003</h3><ol><li>\u8A8D\u8B58\u907A\u7522\u5730\u7CBE\u795E\u7531\u6709\u5F62\uFF08\u907A\u5740\u3001\u5EFA\u7BC9\u7269\u3001\u666F\u89C0\u3001\u7DDA\u8DEF\u3001\u53EF\u79FB\u52D5\u6587\u7269\uFF09\u548C\u7121\u5F62\u5143\u7D20\uFF08\u8A18\u61B6\u3001\u53E3\u8FF0\u6B77\u53F2\u3001\u66F8\u9762\u6587\u737B\u3001\u5100\u5F0F\u3001\u6176\u5178\u6D3B\u52D5\u3001\u50B3\u7D71\u77E5\u8B58\u3001\u50F9\u503C\u3001\u8CEA\u5730\u3001\u984F\u8272\u3001\u6C23\u5473\u7B49\uFF09\u69CB\u6210\u3002\u9019\u4E9B\u5143\u7D20\u5C0D\u907A\u7522\u5730\u7684\u5F62\u6210\u548C\u8CE6\u4E88\u907A\u7522\u5730\u7CBE\u795E\u6709\u8457\u91CD\u5927\u7684\u8CA2\u737B\u3002\u6211\u5011\u5BA3\u4F48\uFF0C\u7121\u5F62\u6587\u5316\u907A\u7522\u7D66\u4E88\u4E86\u907A\u7522\u6574\u9AD4\u66F4\u52A0\u8C50\u5BCC\u3001\u5B8C\u6574\u7684\u610F\u7FA9\uFF0C\u5FC5\u9808\u5C07\u5176\u8003\u616E\u5728\u6240\u6709\u7684\u6587\u5316\u907A\u7522\u76F8\u95DC\u7ACB\u6CD5\uFF0C\u4EE5\u53CA\u53E4\u8DE1\u3001\u907A\u5740\u3001\u666F\u89C0\u3001\u7DDA\u8DEF\u548C\u85CF\u54C1\u7684\u4FDD\u8B77\u8207\u4FEE\u5FA9\u5DE5\u4F5C\u4E2D\u3002</li><li>\u7531\u65BC\u907A\u7522\u5730\u7CBE\u795E\u8907\u96DC\u4E14\u5F62\u5F0F\u591A\u6A23\uFF0C\u70BA\u66F4\u597D\u5730\u7406\u89E3\u3001\u4FDD\u8B77\u548C\u50B3\u627F\u907A\u7522\u5730\u7CBE\u795E\uFF0C\u6211\u5011\u6566\u4FC3\u5404\u570B\u653F\u5E9C\u548C\u5229\u76CA\u76F8\u95DC\u8005\uFF0C\u5411\u8DE8\u5B78\u79D1\u7814\u7A76\u5718\u968A\u548C\u50B3\u7D71\u5F9E\u696D\u4EBA\u54E1\u8ACB\u6559\u5C08\u696D\u77E5\u8B58\u548C\u6280\u80FD\u3002</li><li>\u7531\u65BC\u907A\u7522\u5730\u7CBE\u795E\u7684\u5F62\u6210\u662F\u61C9\u793E\u5340\u8B8A\u5316\u548C\u6301\u7E8C\u767C\u5C55\u4E4B\u9700\u800C\u4E0D\u65B7\u91CD\u69CB\u7684\u904E\u7A0B\uFF0C\u6211\u5011\u8A8D\u70BA\uFF0C\u907A\u7522\u5730\u7CBE\u795E\u6703\u6839\u64DA\u793E\u6703\u7FA4\u9AD4\u7684\u8A18\u61B6\u7FD2\u6163\u3001\u96A8\u8457\u6642\u9593\u63A8\u79FB\u548C\u6587\u5316\u8B8A\u5316\u800C\u6539\u8B8A\uFF1B\u540C\u6642\uFF0C\u4E00\u500B\u907A\u7522\u5730\u53EF\u4EE5\u64C1\u6709\u6578\u7A2E\u7CBE\u795E\uFF0C\u4E26\u7531\u4E0D\u540C\u7FA4\u9AD4\u5171\u7528\u3002</li></ol><h3 id="\u8A8D\u6E05\u5C0D\u907A\u7522\u5730\u7CBE\u795E\u7684\u5A01\u8105" tabindex="-1"><a class="header-anchor" href="#\u8A8D\u6E05\u5C0D\u907A\u7522\u5730\u7CBE\u795E\u7684\u5A01\u8105" aria-hidden="true">#</a> \u8A8D\u6E05\u5C0D\u907A\u7522\u5730\u7CBE\u795E\u7684\u5A01\u8105</h3><ol><li>\u7531\u65BC\u6C23\u5019\u8B8A\u5316\u3001\u5927\u898F\u6A21\u65C5\u904A\u3001\u8ECD\u4E8B\u885D\u7A81\u548C\u57CE\u5E02\u958B\u767C\u5C0E\u81F4\u793E\u6703\u8B8A\u9077\u548C\u89E3\u9AD4\uFF0C\u6211\u5011\u9700\u8981\u66F4\u52A0\u5168\u9762\u5730\u7406\u89E3\u9019\u4E9B\u5A01\u8105\uFF0C\u4EE5\u63D0\u51FA\u9810\u9632\u63AA\u65BD\u548C\u53EF\u6301\u7E8C\u7684\u89E3\u6C7A\u65B9\u6848\u3002\u6211\u5011\u5EFA\u8B70\u653F\u5E9C\u548C\u975E\u653F\u5E9C\u6A5F\u69CB\u3001\u5730\u65B9\u548C\u570B\u5BB6\u907A\u7522\u7D44\u7E54\uFF0C\u5236\u5B9A\u9577\u671F\u6230\u7565\u8A08\u756B\uFF0C\u9810\u9632\u907A\u7522\u5730\u7CBE\u795E\u53CA\u5176\u74B0\u5883\u7684\u60E1\u5316\u3002\u6B64\u5916\uFF0C\u9084\u61C9\u5411\u5C45\u6C11\u548C\u5730\u65B9\u7576\u5C40\u5BA3\u50B3\u907A\u7522\u5730\u7CBE\u795E\u4FDD\u8B77\uFF0C\u4F7F\u4ED6\u5011\u5C0D\u4E0D\u65B7\u8B8A\u9077\u7684\u4E16\u754C\u5E36\u4F86\u7684\u5A01\u8105\u505A\u597D\u66F4\u5145\u5206\u7684\u6E96\u5099\u3002</li><li>\u7531\u65BC\u4E0D\u540C\u7FA4\u9AD4\u5171\u7528\u907A\u7522\u5730\u6703\u70BA\u5176\u5E36\u4F86\u591A\u7A2E\u4E0D\u540C\u7684\u7CBE\u795E\uFF0C\u4E5F\u589E\u52A0\u4E86\u7AF6\u722D\u548C\u885D\u7A81\u7684\u98A8\u96AA\uFF0C\u6211\u5011\u8A8D\u8B58\u5230\uFF0C\u9019\u4E9B\u907A\u7522\u5730\u9700\u8981\u7279\u5B9A\u7684\u7BA1\u7406\u898F\u5283\u548C\u7B56\u7565\uFF0C\u4EE5\u9069\u61C9\u73FE\u4EE3\u591A\u6587\u5316\u793E\u6703\u7684\u591A\u5143\u74B0\u5883\u3002\u56E0\u70BA\u5C0D\u907A\u7522\u5730\u7CBE\u795E\u7684\u5A01\u8105\u5728\u5C11\u6578\u7FA4\u9AD4\u4E2D\u5C24\u5176\u7A81\u51FA\uFF0C\u7121\u8AD6\u4ED6\u5011\u662F\u672C\u571F\u5C45\u6C11\u6216\u5916\u4F86\u8005\uFF0C\u6211\u5011\u90FD\u5EFA\u8B70\uFF0C\u9019\u4E9B\u7FA4\u9AD4\u61C9\u7576\u53D7\u5230\u7279\u5B9A\u653F\u7B56\u548C\u63AA\u65BD\u7684\u6700\u512A\u5148\u548C\u9996\u8981\u7684\u7167\u9867\u3002</li></ol><h3 id="\u907A\u7522\u5730\u7CBE\u795E\u7684\u4FDD\u8B77" tabindex="-1"><a class="header-anchor" href="#\u907A\u7522\u5730\u7CBE\u795E\u7684\u4FDD\u8B77" aria-hidden="true">#</a> \u907A\u7522\u5730\u7CBE\u795E\u7684\u4FDD\u8B77</h3><ol><li>\u7531\u65BC\u7576\u4ECA\u4E16\u754C\u5728\u5927\u90E8\u5206\u570B\u5BB6\u4E2D\uFF0C\u907A\u7522\u5730\u7CBE\u795E\u3001\u7279\u5225\u662F\u5176\u7121\u5F62\u5143\u7D20\uFF0C\u4E26\u672A\u5F9E\u6B63\u898F\u6559\u80B2\u6216\u7ACB\u6CD5\u4FDD\u8B77\u4E2D\u7372\u76CA\uFF0C\u6211\u5011\u5EFA\u8B70\uFF0C\u5EFA\u7ACB\u8AD6\u58C7\uFF0C\u8AEE\u8A62\u4E0D\u540C\u80CC\u666F\u7684\u5C08\u5BB6\u548C\u4F86\u81EA\u7576\u5730\u793E\u5340\u7684\u638C\u63E1\u8C50\u5BCC\u77E5\u8B58\u6280\u80FD\u7684\u4EBA\uFF0C\u5236\u5B9A\u57F9\u8A13\u8A08\u756B\u548C\u6CD5\u5F8B\u653F\u7B56\uFF0C\u4EE5\u66F4\u597D\u5730\u4FDD\u8B77\u548C\u5BA3\u50B3\u907A\u7522\u5730\u7CBE\u795E\u3002</li><li>\u8003\u616E\u5230\u73FE\u4EE3\u6578\u4F4D\u6280\u8853\uFF08\u8CC7\u6599\u5EAB\u3001\u7DB2\u7AD9\uFF09\u80FD\u5920\u4F4E\u6210\u672C\u3001\u9AD8\u6548\u7387\u5730\u958B\u767C\u6574\u5408\u907A\u7522\u6709\u5F62\u548C\u7121\u5F62\u5143\u7D20\u7684\u591A\u5A92\u9AD4\u8CC7\u8A0A\uFF0C\u70BA\u4E86\u66F4\u597D\u5730\u4FDD\u8B77\u3001\u5BA3\u50B3\u548C\u4FC3\u9032\u907A\u7522\u5730\u53CA\u5176\u7CBE\u795E\uFF0C\u6211\u5011\u5F37\u70C8\u5EFA\u8B70\u5EE3\u6CDB\u904B\u7528\u6B64\u985E\u6280\u8853\u3002\u5B83\u5C07\u63A8\u52D5\u907A\u7522\u5730\u7CBE\u795E\u8A18\u9304\u7684\u591A\u6A23\u6027\u767C\u5C55\u548C\u6301\u7E8C\u66F4\u65B0\u3002</li></ol><h3 id="\u907A\u7522\u5730\u7CBE\u795E\u7684\u50B3\u627F" tabindex="-1"><a class="header-anchor" href="#\u907A\u7522\u5730\u7CBE\u795E\u7684\u50B3\u627F" aria-hidden="true">#</a> \u907A\u7522\u5730\u7CBE\u795E\u7684\u50B3\u627F</h3><ol><li>\u8A8D\u8B58\u5230\u907A\u7522\u5730\u7CBE\u795E\u4E3B\u8981\u7531\u4EBA\u985E\u50B3\u627F\uFF0C\u800C\u50B3\u627F\u53C8\u662F\u4FDD\u8B77\u5B83\u7684\u91CD\u8981\u7D44\u6210\u90E8\u5206\uFF0C\u6211\u5011\u5F37\u8ABF\uFF0C\u901A\u904E\u4E92\u52D5\u4EA4\u6D41\u548C\u76F8\u95DC\u793E\u5340\u7684\u53C3\u8207\uFF0C\u907A\u7522\u5730\u7CBE\u795E\u80FD\u5920\u5F97\u5230\u6700\u6709\u6548\u7684\u4FDD\u8B77\u3001\u4F7F\u7528\u548C\u978F\u56FA\u3002\u4EA4\u6D41\u662F\u4FDD\u6301\u907A\u7522\u5730\u7CBE\u795E\u751F\u751F\u4E0D\u606F\u7684\u6700\u4F73\u9014\u5F91\u3002</li><li>\u9452\u65BC\u907A\u7522\u5730\u6240\u5728\u793E\u5340\uFF0C\u5C24\u5176\u662F\u50B3\u7D71\u6587\u5316\u7FA4\u9AD4\uFF0C\u901A\u5E38\u6700\u80FD\u5920\u7406\u89E3\u907A\u7522\u5730\u7CBE\u795E\uFF0C\u56E0\u800C\u6211\u5011\u8A8D\u70BA\uFF0C\u4ED6\u5011\u6700\u9069\u5408\u4FDD\u8B77\u907A\u7522\u5730\u7CBE\u795E\uFF0C\u4E14\u61C9\u8207\u6240\u6709\u4FDD\u8B77\u548C\u50B3\u627F\u907A\u7522\u5730\u7CBE\u795E\u7684\u52AA\u529B\u5BC6\u5207\u76F8\u95DC\u3002\u61C9\u9F13\u52F5\u63A1\u53D6\u5404\u7A2E\u975E\u6B63\u5F0F\uFF08\u53E3\u8FF0\u3001\u5100\u5F0F\u3001\u8868\u6F14\u3001\u50B3\u7D71\u7D93\u9A57\u548C\u5BE6\u8E10\u7B49\uFF09\u548C\u6B63\u5F0F\uFF08\u6559\u80B2\u5C08\u6848\u3001\u6578\u4F4D\u8CC7\u6599\u5EAB\u3001\u7DB2\u7AD9\u3001\u6559\u5B78\u5DE5\u5177\u3001\u591A\u5A92\u9AD4\u6F14\u793A\u7B49\uFF09\u7684\u50B3\u627F\u65B9\u6CD5\uFF0C\u56E0\u70BA\u5B83\u5011\u4E0D\u50C5\u80FD\u5920\u78BA\u4FDD\u907A\u7522\u5730\u7CBE\u795E\u7684\u4FDD\u8B77\uFF0C\u66F4\u91CD\u8981\u7684\u662F\u80FD\u5920\u4FDD\u8B77\u793E\u5340\u7684\u53EF\u6301\u7E8C\u6027\u793E\u6703\u767C\u5C55\u3002</li><li>\u8A8D\u8B58\u5230\u8DE8\u4E16\u4EE3\u548C\u8DE8\u6587\u5316\u50B3\u627F\u5728\u6301\u7E8C\u5BA3\u50B3\u548C\u4FDD\u8B77\u907A\u7522\u5730\u7CBE\u795E\u65B9\u9762\u6240\u626E\u6F14\u7684\u91CD\u8981\u89D2\u8272\uFF0C\u6211\u5011\u5EFA\u8B70\uFF0C\u5E74\u8F15\u4E00\u4EE3\u4EE5\u53CA\u548C\u907A\u7522\u5730\u6709\u95DC\u7684\u4E0D\u540C\u6587\u5316\u7FA4\u9AD4\u5728\u907A\u7522\u5730\u7CBE\u795E\u7684\u653F\u7B56\u5236\u5B9A\u548C\u7BA1\u7406\u4E0A\u80FD\u5920\u589E\u9032\u5408\u4F5C\u4E26\u53C3\u8207\u5176\u4E2D\u3002</li></ol><h2 id="quebec-declaration-on-the-preservation-of-the-spirit-of-place" tabindex="-1"><a class="header-anchor" href="#quebec-declaration-on-the-preservation-of-the-spirit-of-place" aria-hidden="true">#</a> Qu\xE9bec Declaration on the Preservation of the Spirit of Place</h2><p><em>Adopted at Qu\xE9bec, Canada, October 4th 2008</em></p><h3 id="preamble" tabindex="-1"><a class="header-anchor" href="#preamble" aria-hidden="true">#</a> Preamble</h3><p>Meeting in the historic city of Qu\xE9bec (Canada), from 29 September to 4 October 2008, at the invitation of ICOMOS Canada, on the occasion of the 16th General Assembly of ICOMOS and the celebrations marking the 400th anniversary of the founding of Qu\xE9bec, the participants adopt the following Declaration of principles and recommendations to preserve the spirit of place through the safeguarding of tangible and intangible heritage, which is regarded as an innovative and efficient manner of ensuring sustainable and social development throughout the world.</p><p>This Declaration is part of a series of measures and actions undertaken by ICOMOS over the course of the past five years to safeguard and promote the spirit of places, namely their living, social and spiritual nature. In 2003, ICOMOS focused the scientific symposium of its 14th General Assembly on the theme of the preservation of social intangible values of monuments and sites. In the ensuing Kimberly Declaration, ICOMOS committed itself to taking into account the intangible values (memory, beliefs, traditional knowledge, attachment to place) and the local communities that are the custodians of these values in the management and preservation of monuments and sites under the World Heritage Convention of 1972. The ICOMOS Xi\u2019an Declaration of 2005 draws attention to the conservation of context, defined as the physical, visual and natural aspects as well as social and spiritual practices, customs, traditional knowledge and other intangible forms and expressions, in the protection and promotion of world heritage monuments and sites. It also calls upon a multidisciplinary approach and diversified sources of information in order to better understand, manage and conserve context. The Declaration of Foz Do Igua\xE7u, drawn up in 2008 by ICOMOS Americas, specifies that the tangible and intangible components of heritage are essential in the preservation of the identity of communities that have created and transmitted spaces of cultural and historical significance. The new ICOMOS charters on Cultural Routes and on Interpretation and Presentation, formulated after extensive consultations and presented for ratification at the present 16th ICOMOS General Assembly, also recognize the importance of intangible dimensions of heritage and the spiritual value of place. Because of the indivisible nature of tangible and intangible heritage and the meanings, values and context intangible heritage gives to objects and places, ICOMOS is currently considering the adoption of a new charter dedicated specifically to the intangible heritage of monuments and sites. In this regard, we encourage discussion and debates in order to develop a new conceptual vocabulary that takes into account the ontological changes of the spirit of place.</p><p>The 16th General Assembly, and more specifically the Youth Forum, the Aboriginal Forum and the Scientific Symposium, have provided an opportunity to further explore the relationship between tangible and intangible heritage, and the internal social and cultural mechanisms of the spirit of place. Spirit of place is defined as the tangible (buildings, sites, landscapes, routes, objects) and the intangible elements (memories, narratives, written documents, rituals, festivals, traditional knowledge, values, textures, colors, odors, etc.), that is to say the physical and the spiritual elements that give meaning, value, emotion and mystery to place. Rather than separate spirit from place, the intangible from the tangible, and consider them as opposed to each other, we have investigated the many ways in which the two interact and mutually construct one another. The spirit of place is constructed by various social actors, its architects and managers as well as its users, who all contribute actively and concurrently to giving it meaning. Considered as a relational concept, spirit of place takes on a plural and dynamic character, capable of possessing multiple meanings and singularities, of changing through time, and of belonging to different groups. This more dynamic approach is also better adapted to today\u2019s globalized world, which is characterized by transnational population movements, relocated populations, increased intercultural contacts, pluralistic societies, and multiple attachments to place.</p><p>The spirit of place offers a more comprehensive understanding of the living and, at the same time, permanent character of monuments, sites and cultural landscapes. It provides a richer, more dynamic, and inclusive vision of cultural heritage. Spirit of place exists, in one form or another, in practically all the cultures of the world, and is constructed by human beings in response to their social needs. The communities that inhabit place, especially when they are traditional societies, should be intimately associated in the safeguarding of its memory, vitality, continuity and spirituality.</p><p>The participants of the 16th General Assembly of ICOMOS therefore address the following Declaration of principles and recommendations to intergovernmental and non-governmental organizations, national and local authorities and all institutions and specialists in a position to contribute through legislation, policies, planning processes and management to better protecting and promoting the spirit of place.</p><h3 id="rethinking-the-spirit-of-place" tabindex="-1"><a class="header-anchor" href="#rethinking-the-spirit-of-place" aria-hidden="true">#</a> Rethinking the Spirit of Place</h3><ol><li>Recognizing that the spirit of place is made up of tangible (sites, buildings, landscapes, routes, objects) as well as intangible elements (memories, narratives, written documents, festivals, commemorations, rituals, traditional knowledge, values, textures, colors, odors, etc.), which all significantly contribute to making place and to giving it spirit, we declare that intangible cultural heritage gives a richer and more complete meaning to heritage as a whole and it must be taken into account in all legislation concerning cultural heritage, and in all conservation and restoration projects for monuments, sites, landscapes, routes and collections of objects.</li><li>Because the spirit of place is complex and multiform, we demand that governments and other stakeholders call upon the expertise of multidisciplinary research teams and traditional practitioners in order to better understand, preserve and transmit the spirit of place.</li><li>Since the spirit of place is a continuously reconstructed process, which responds to the needs for change and continuity of communities, we uphold that it can vary in time and from one culture to another according to their practices of memory, and that a place can have several spirits and be shared by different groups.</li></ol><h3 id="identifying-the-threats-to-the-spirit-of-place" tabindex="-1"><a class="header-anchor" href="#identifying-the-threats-to-the-spirit-of-place" aria-hidden="true">#</a> Identifying the Threats to the Spirit of Place</h3><ol><li>Since climatic change, mass tourism, armed conflict and urban development lead to the transformation and disruption of societies, we need to better understand these threats in order to establish preventive measures and sustainable solutions. We recommend that governmental and non-governmental agencies, and local and national heritage organizations develop long term strategic plans to prevent the degradation of the spirit of place and its environment. The inhabitants and local authorities should also be made aware of the safeguarding of the spirit of place so that they are better prepared to deal with the threats of a changing world.</li><li>As the sharing of places invested with different spirits by several groups increases the risk of competition and conflict, we recognize that these sites require specific management plans and strategies, adapted to the pluralistic context of modern multicultural societies. Because the threats to the spirit of place are especially high amongst minority groups, be they natives or newcomers, we recommend that these groups benefit first and foremost from specific policies and practices.</li></ol><h3 id="safeguarding-the-spirit-of-place" tabindex="-1"><a class="header-anchor" href="#safeguarding-the-spirit-of-place" aria-hidden="true">#</a> Safeguarding the Spirit of Place</h3><ol><li>Because in most countries of the world today the spirit of place, in particular its intangible components, do not currently benefit from formal educational programs or legal protection, we recommend the setting up of forums and consultations with experts from different backgrounds and resource persons from local communities, and the development of training programs and legal policies in order to better safeguard and promote the spirit of place.</li><li>Considering that modern digital technologies (digital databases, websites) can be used efficiently and effectively at a low cost to develop multimedia inventories that integrate tangible and intangible elements of heritage, we strongly recommend their widespread use in order to better preserve, disseminate and promote heritage places and their spirit. These technologies facilitate the diversity and constant renewal of the documentation on the spirit of place.</li></ol><h3 id="transmitting-the-spirit-of-place" tabindex="-1"><a class="header-anchor" href="#transmitting-the-spirit-of-place" aria-hidden="true">#</a> Transmitting the Spirit of Place</h3><ol><li>Recognizing that spirit of place is transmitted essentially by people, and that transmission is an important part of its conservation, we declare that it is through interactive communication and the participation of the concerned communities that the spirit of place is most efficiently safeguarded, used and enhanced. Communication is the best tool for keeping the spirit of place alive.</li><li>Given that local communities are generally in the best position to comprehend the spirit of place, especially in the case of traditional cultural groups, we maintain that they are also best equipped to safeguard it and should be intimately associated in all endeavors to preserve and transmit the spirit of place. Non-formal (narratives, rituals, performances, traditional experience and practices, etc.) and formal (educational programs, digital databases, websites, pedagogical tools, multimedia presentations, etc.) means of transmission should be encouraged because they ensure not only the safeguarding of the spirit of place but, more importantly, the sustainable and social development of the community.</li><li>Recognizing that intergenerational and transcultural transmission plays an important role in the sustained dissemination and the preservation of the spirit of place, we recommend the association and involvement of younger generations, as well as different cultural groups associated with the site, in policy-making and the management of the spirit of place.</li></ol>',32);function f(g,b){const a=n("ExternalLinkIcon");return o(),r("div",null,[e("blockquote",null,[e("p",null,[d,e("a",h,[p,s(a)]),m])]),u])}var w=i(c,[["render",f],["__file","2008-quebec-declaration-on-the-preservation-of-the-spirit-of-place.html.vue"]]);export{w as default};
