import{_ as e,d as t}from"./app.d97fbafd.js";const i={},a=t('<h1 id="_1970\u5E74\u516C\u7D04" tabindex="-1"><a class="header-anchor" href="#_1970\u5E74\u516C\u7D04" aria-hidden="true">#</a> 1970\u5E74\u516C\u7D04</h1><blockquote></blockquote><h2 id="\u95DC\u65BC\u7981\u6B62\u548C\u9632\u6B62\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u7684\u65B9\u6CD5\u7684\u516C\u7D04" tabindex="-1"><a class="header-anchor" href="#\u95DC\u65BC\u7981\u6B62\u548C\u9632\u6B62\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u7684\u65B9\u6CD5\u7684\u516C\u7D04" aria-hidden="true">#</a> \u95DC\u65BC\u7981\u6B62\u548C\u9632\u6B62\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u7684\u65B9\u6CD5\u7684\u516C\u7D04</h2><p>1970\u5E7411\u670817\u65E5\u8A02\u65BC\u5DF4\u9ECE</p><p>\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u65BC1970\u5E7410\u670812\u65E5\u81F311\u670814\u65E5\u5728\u5DF4\u9ECE\u53EC\u958B\u7B2C\u5341\u516D\u5C46\u5927\u6703\uFF0C</p><p>\u61B6\u53CA\u5176\u7B2C\u5341\u56DB\u5C46\u5927\u6703\u901A\u904E\u7684\u300A\u570B\u969B\u6587\u5316\u5408\u4F5C\u539F\u5247\u5BA3\u8A00\u300B\u6240\u8F09\u898F\u5B9A\u7684\u91CD\u8981\u6027\u3002</p><p>\u8003\u616E\u5230\u5404\u570B\u9593\u70BA\u79D1\u5B78\u3001\u6587\u5316\u53CA\u6559\u80B2\u76EE\u7684\u800C\u9032\u884C\u7684\u6587\u5316\u8CA1\u7522\u4EA4\u6D41\u589E\u9032\u4E86\u4EBA\u985E\u6587\u660E\u7684\u8A8D\u8B58\u3001\u8C50\u5BCC\u4E86\u5404\u570B\u4EBA\u6C11\u7684\u6587\u5316\u751F\u6D3B\u4E26\u6FC0\u767C\u4E86\u5404\u570B\u4E4B\u9593\u7684\u76F8\u4E92\u5C0A\u91CD\u548C\u4E86\u89E3\u3002</p><p>\u8003\u616E\u5230\u6587\u5316\u8CA1\u7522\u5BE6\u70BA\u69CB\u6210\u6587\u660E\u548C\u6C11\u65CF\u6587\u5316\u7684\u4E00\u5927\u57FA\u672C\u8981\u7D20\uFF0C\u53EA\u6709\u76E1\u53EF\u80FD\u5145\u5206\u638C\u63E1\u6709\u95DC\u5176\u8D77\u6E90\u3001\u6B77\u53F2\u548C\u50B3\u7D71\u80CC\u666F\u7684\u77E5\u8B58\uFF0C\u624D\u80FD\u7406\u89E3\u5176\u771F\u6B63\u50F9\u503C\u3002</p><p>\u8003\u616E\u5230\u5404\u570B\u6709\u8CAC\u4EFB\u4FDD\u8B77\u5176\u9818\u571F\u4E0A\u7684\u6587\u5316\u8CA1\u7522\u514D\u53D7\u5077\u76DC\u3001\u79D8\u5BC6\u767C\u6398\u548C\u975E\u6CD5\u51FA\u53E3\u7684\u5371\u96AA\uFF0C</p><p>\u8003\u616E\u5230\u70BA\u907F\u514D\u9019\u4E9B\u5371\u96AA\uFF0C\u5404\u570B\u5FC5\u9808\u65E5\u76CA\u8A8D\u8B58\u5230\u4E26\u5C0A\u91CD\u672C\u570B\u53CA\u5176\u4ED6\u6240\u6709\u570B\u5BB6\u7684\u6587\u5316\u907A\u7522\u7684\u9053\u7FA9\u8CAC\u4EFB\u3002</p><p>\u8003\u616E\u5230\u535A\u7269\u9928\u3001\u5716\u66F8\u9928\u548C\u6A94\u6848\u9928\u4F5C\u70BA\u6587\u5316\u6A5F\u69CB\u61C9\u4FDD\u8B49\u6839\u64DA\u666E\u904D\u516C\u8A8D\u7684\u9053\u7FA9\u539F\u5247\u532F\u96C6\u5176\u6536\u85CF\u54C1\uFF0C</p><p>\u8003\u616E\u5230\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u963B\u7919\u4E86\u5404\u570B\u4E4B\u9593\u7684\u8AD2\u89E3\uFF0C\u6559\u79D1\u6587\u7D44\u7E54\u7684\u4E00\u90E8\u5206\u8077\u8CAC\u5C31\u662F\u901A\u904E\u5411\u6709\u95DC\u570B\u5BB6\u63A8\u85A6\u9019\u65B9\u9762\u7684\u5404\u9805\u570B\u969B\u516C\u7D04\u4EE5\u4FC3\u9032\u9019\u4E00\u8AD2\u89E3\uFF0C</p><p>\u8003\u616E\u5230\u53EA\u6709\u5404\u570B\u5728\u570B\u5BB6\u548C\u570B\u969B\u7BC4\u570D\u4E0A\u9032\u884C\u7D44\u7E54\uFF0C\u5BC6\u5207\u5408\u4F5C\uFF0C\u624D\u80FD\u6709\u6548\u4FDD\u8B77\u6587\u5316\u907A\u7522\uFF0C</p><p>\u8003\u616E\u5230\u6559\u79D1\u6587\u7D44\u7E54\u5927\u6703\u57281964\u5E74\u5C31\u6B64\u901A\u904E\u4E86\u4E00\u9805\u5EFA\u8B70\uFF0C</p><p>\u5DF2\u6536\u5230\u95DC\u65BC\u7981\u6B62\u548C\u9632\u6B62\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u7684\u65B9\u6CD5\u7684\u5404\u9805\u9032\u4E00\u6B65\u5EFA\u8B70\uFF0C\u9019\u4E00\u554F\u984C\u696D\u5DF2\u4F5C\u70BA\u7B2C\u5341\u4E5D\u9805\u8B70\u7A0B\u9805\u76EE\u5217\u5165\u672C\u5C46\u6703\u8B70\u8B70\u7A0B\uFF0C</p><p>\u7B2C\u5341\u4E94\u5C46\u6703\u8B70\u5DF2\u6C7A\u5B9A\u5C31\u9019\u4E00\u554F\u984C\u5236\u8A02\u4E00\u9805\u570B\u969B\u516C\u7D04\uFF0C</p><p>\u57281970\u5E7411\u670814\u65E5\u901A\u904E\u672C\u516C\u7D04\u3002</p><h3 id="\u7B2C\u4E00\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u689D" aria-hidden="true">#</a> \u7B2C\u4E00\u689D</h3><p>\u70BA\u4E86\u672C\u516C\u7D04\u7684\u76EE\u7684\uFF0C\u300C\u6587\u5316\u8CA1\u7522\u300D\u4E00\u8A5E\u7CFB\u6307\u6BCF\u500B\u570B\u5BB6\uFF0C\u6839\u64DA\u5B97\u6559\u7684\u6216\u4E16\u4FD7\u7684\u7406\u7531\uFF0C\u660E\u78BA\u6307\u5B9A\u70BA\u5177\u6709\u91CD\u8981\u8003\u53E4\u3001\u53F2\u524D\u53F2\u3001\u6B77\u53F2\u3001\u6587\u5B78\u3001\u85DD\u8853\u6216\u79D1\u5B78\u50F9\u503C\u7684\u8CA1\u7522\u4E26\u5C6C\u65BC\u4E0B\u5217\u5404\u985E\u8005\uFF1A</p><ol><li>\u52D5\u7269\u7FA4\u843D\u3001\u690D\u7269\u7FA4\u843D\u3001\u7926\u7269\u548C\u89E3\u5256\u4EE5\u53CA\u5177\u6709\u53E4\u751F\u7269\u5B78\u610F\u7FA9\u7684\u7269\u54C1\u7684\u7A00\u6709\u6536\u96C6\u54C1\u548C\u6A19\u672C\uFF1B</li><li>\u6709\u95DC\u6B77\u53F2\uFF0C\u5305\u62EC\u79D1\u5B78\u3001\u6280\u8853\u3001\u8ECD\u4E8B\u53CA\u793E\u6703\u53F2\u3001\u6709\u95DC\u570B\u5BB6\u9818\u8896\u3001\u601D\u60F3\u5BB6\u3001\u79D1\u5B78\u5BB6\u3001\u85DD\u8853\u5BB6\u4E4B\u751F\u5E73\u4EE5\u53CA\u6709\u95DC\u570B\u5BB6\u91CD\u5927\u4E8B\u4EF6\u7684\u8CA1\u7522\uFF1B</li><li>\u8003\u53E4\u767C\u6398\uFF08\u5305\u62EC\u6B63\u5E38\u7684\u548C\u79D8\u5BC6\u7684\uFF09\u6216\u8003\u53E4\u767C\u73FE\u7684\u6210\u679C\uFF1B</li><li>\u696D\u5DF2\u80A2\u89E3\u7684\u85DD\u8853\u6216\u6B77\u53F2\u53E4\u8E5F\u6216\u8003\u53E4\u907A\u5740\u4E4B\u69CB\u6210\u90E8\u5206\uFF1B</li><li>\u4E00\u767E\u5E74\u4EE5\u524D\u7684\u53E4\u7269\uFF0C\u5982\u9298\u6587\u3001\u9322\u5E63\u548C\u5370\u7AE0\uFF1B</li><li>\u5177\u6709\u4EBA\u7A2E\u5B78\u610F\u7FA9\u7684\u6587\u7269\uFF1B</li><li>\u6709\u85DD\u8853\u50F9\u503C\u7684\u8CA1\u7522\uFF0C\u5982 <ul><li>\u5168\u90E8\u662F\u624B\u5DE5\u5B8C\u6210\u7684\u5716\u756B\u3001\u7E6A\u756B\u548C\u7E6A\u5716\uFF0C\u4E0D\u8AD6\u5176\u88DD\u5E40\u6846\u5EA7\u5982\u4F55\uFF0C\u4E5F\u4E0D\u8AD6\u7528\u7684\u662F\u4F55\u7A2E\u6750\u6599\uFF08\u4E0D\u5305\u62EC\u5DE5\u696D\u8A2D\u8A08\u5716\u53CA\u624B\u5DE5\u88DD\u98FE\u7684\u5DE5\u696D\u7522\u54C1\uFF09\uFF1B</li><li>\u7528\u65BC\u4EFB\u4F55\u6750\u6599\u88FD\u6210\u7684\u96D5\u5851\u85DD\u8853\u548C\u96D5\u523B\u7684\u539F\u4F5C\uFF1B</li><li>\u7248\u756B\u3001\u5370\u7247\u548C\u5E73\u7248\u756B\u7684\u539F\u4EF6\uFF1B</li><li>\u7528\u4EFB\u4F55\u6750\u6599\u7D44\u96C6\u6216\u62FC\u96C6\u7684\u85DD\u8853\u54C1\u539F\u4F5C\uFF1B</li></ul></li><li>\u7A00\u6709\u624B\u7A3F\u548C\u53E4\u7248\u66F8\u7C4D\uFF0C\u6709\u7279\u6B8A\u610F\u7FA9\u7684\uFF08\u6B77\u53F2\u3001\u85DD\u8853\u3001\u79D1\u5B78\u3001\u6587\u5B78\u7B49\uFF09\u53E4\u66F8\u3001\u6587\u4EF6\u548C\u51FA\u7248\u7269\uFF0C\u4E0D\u8AD6\u662F\u55AE\u672C\u7684\u6216\u6574\u5957\u7684\uFF1B</li><li>\u90F5\u7968\u3001\u5370\u82B1\u7A05\u7968\u53CA\u985E\u4F3C\u7684\u7968\u8B49\uFF0C\u4E0D\u8AD6\u662F\u55AE\u5F35\u7684\u6216\u6210\u5957\u7684\uFF1B</li><li>\u6A94\u6848\uFF0C\u5305\u62EC\u6709\u8072\u3001\u7167\u76F8\u548C\u96FB\u5F71\u6A94\u6848\uFF1B</li><li>\u4E00\u767E\u5E74\u4EE5\u524D\u7684\u5BB6\u5177\u7269\u54C1\u548C\u53E4\u6A02\u5668\u3002</li></ol><h3 id="\u7B2C\u4E8C\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u689D</h3><ol><li>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u8A8D\u6587\u5316\u8CA1\u7522\u975E\u6CD5\u9032\u51FA\u53E3\u548C\u6240\u6709\u6B0A\u975E\u6CD5\u8F49\u8B93\u662F\u9020\u6210\u9019\u985E\u8CA1\u7522\u7684\u539F\u4E3B\u570B\u5BB6\u6587\u5316\u907A\u7522\u67AF\u7AED\u7684\u4E3B\u8981\u539F\u56E0\u4E4B\u4E00\uFF0C\u4E26\u627F\u8A8D\u570B\u969B\u5408\u4F5C\u662F\u4FDD\u8B77\u5404\u570B\u6587\u5316\u8CA1\u7522\u514D\u906D\u7531\u6B64\u7522\u751F\u7684\u5404\u7A2E\u5371\u96AA\u7684\u6700\u6709\u6548\u65B9\u6CD5\u4E4B\u4E00\u3002</li><li>\u70BA\u6B64\u76EE\u7684\uFF0C\u7DE0\u7D04\u570B\u627F\u64D4\u5229\u7528\u73FE\u6709\u624B\u6BB5\uFF0C\u7279\u5225\u662F\u901A\u904E\u6D88\u9664\u5176\u6839\u6E90\u3001\u5236\u6B62\u73FE\u6709\u505A\u6CD5\u548C\u5E6B\u52A9\u7D66\u4E88\u5FC5\u8981\u7684\u88DC\u511F\u4F86\u53CD\u5C0D\u9019\u7A2E\u505A\u6CD5\u3002</li></ol><h3 id="\u7B2C\u4E09\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u689D" aria-hidden="true">#</a> \u7B2C\u4E09\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u9055\u53CD\u672C\u516C\u7D04\u6240\u5217\u7684\u898F\u5B9A\u800C\u9020\u6210\u7684\u6587\u5316\u8CA1\u7522\u4E4B\u9032\u51FA\u53E3\u6216\u6240\u6709\u6B0A\u8F49\u8B93\u5747\u5C6C\u975E\u6CD5\u3002</p><h3 id="\u7B2C\u56DB\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u689D" aria-hidden="true">#</a> \u7B2C\u56DB\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u8A8D\uFF0C\u70BA\u4E86\u672C\u516C\u7D04\u7684\u5B97\u65E8\uFF0C\u51E1\u5C6C\u4EE5\u4E0B\u5404\u985E\u8CA1\u7522\u5747\u70BA\u6BCF\u500B\u7DE0\u7D04\u570B\u7684\u6587\u5316\u907A\u7522\u7684\u4E00\u90E8\u5206\uFF1A</p><ol><li>\u6709\u95DC\u570B\u5BB6\u7684\u570B\u6C11\u7684\u500B\u4EBA\u6216\u96C6\u9AD4\u5929\u624D\u6240\u5275\u9020\u7684\u6587\u5316\u8CA1\u7522\u548C\u5C45\u4F4F\u5728\u8A72\u570B\u9818\u571F\u5883\u5167\u7684\u5916\u570B\u570B\u6C11\u6216\u7121\u570B\u7C4D\u4EBA\u5728\u8A72\u570B\u9818\u571F\u5167\u5275\u9020\u7684\u5C0D\u6709\u95DC\u570B\u5BB6\u5177\u6709\u91CD\u8981\u610F\u7FA9\u7684\u6587\u5316\u8CA1\u7522\uFF1B</li><li>\u5728\u570B\u5BB6\u9818\u571F\u5167\u767C\u73FE\u7684\u6587\u5316\u8CA1\u7522\uFF1B</li><li>\u7D93\u6B64\u985E\u8CA1\u7522\u539F\u4E3B\u570B\u4E3B\u7BA1\u7576\u5C40\u7684\u540C\u610F\uFF0C\u7531\u8003\u53E4\u5B78\u3001\u4EBA\u7A2E\u5B78\u6216\u81EA\u7136\u79D1\u5B78\u5718\u9AD4\u6240\u7372\u5F97\u7684\u6587\u5316\u8CA1\u7522\uFF1B</li><li>\u7D93\u7531\u81EA\u7531\u9054\u6210\u5354\u8B70\u5BE6\u884C\u4EA4\u6D41\u7684\u6587\u5316\u8CA1\u7522\uFF1B</li><li>\u7D93\u6B64\u985E\u8CA1\u7522\u539F\u4E3B\u570B\u4E3B\u7BA1\u7576\u5C40\u7684\u540C\u610F\uFF0C\u4F5C\u70BA\u8D08\u9001\u54C1\u800C\u63A5\u6536\u7684\u6216\u5408\u6CD5\u8CFC\u7F6E\u7684\u6587\u5316\u8CA1\u7522\u3002</li></ol><h3 id="\u7B2C\u4E94\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u689D" aria-hidden="true">#</a> \u7B2C\u4E94\u689D</h3><p>\u70BA\u78BA\u4FDD\u4FDD\u8B77\u6587\u5316\u8CA1\u7522\u514D\u65BC\u975E\u6CD5\u9032\u51FA\u53E3\u548C\u6240\u6709\u6B0A\u7684\u975E\u6CD5\u8F49\u8B93\uFF0C\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\u82E5\u5C1A\u672A\u8A2D\u7ACB\u4FDD\u8B77\u6587\u5316\u907A\u7522\u7684\u570B\u5BB6\u6A5F\u69CB\uFF0C\u53EF\u6839\u64DA\u672C\u570B\u7684\u60C5\u6CC1\uFF0C\u5728\u5176\u9818\u571F\u4E4B\u5167\u5EFA\u7ACB\u4E00\u500B\u6216\u4E00\u500B\u4EE5\u4E0A\u7684\u570B\u5BB6\u6A5F\u69CB\uFF0C\u914D\u5099\u8DB3\u5920\u4EBA\u6578\u7684\u5408\u683C\u5DE5\u4F5C\u4EBA\u54E1\uFF0C\u4EE5\u6709\u6548\u5730\u884C\u4F7F\u4E0B\u8FF0\u8077\u8CAC\uFF1A</p><ol><li>\u5354\u52A9\u5236\u8A02\u65E8\u5728\u5207\u5BE6\u4FDD\u8B77\u6587\u5316\u907A\u7522\u7279\u5225\u4E8B\u9632\u6B62\u91CD\u8981\u6587\u5316\u8CA1\u7522\u7684\u975E\u6CD5\u9032\u51FA\u53E3\u548C\u975E\u6CD5\u8F49\u8B93\u7684\u6CD5\u5F8B\u548C\u898F\u7AE0\u8349\u6848\uFF1B</li><li>\u6839\u64DA\u5168\u570B\u53D7\u4FDD\u8B77\u8CA1\u7522\u6E05\u518A\uFF0C\u5236\u8A02\u4E26\u4E0D\u65B7\u66F4\u65B0\u4E00\u4EFD\u5176\u51FA\u53E3\u5C07\u9020\u6210\u6587\u5316\u907A\u7522\u7684\u56B4\u91CD\u67AF\u7AED\u7684\u91CD\u8981\u7684\u516C\u5171\u53CA\u79C1\u6709\u6587\u5316\u8CA1\u7522\u7684\u6E05\u55AE\uFF1B</li><li>\u4FC3\u9032\u767C\u5C55\u6216\u6210\u7ACB\u70BA\u4FDD\u8B49\u6587\u5316\u8CA1\u7522\u7684\u4FDD\u5B58\u548C\u5C55\u51FA\u6240\u9700\u4E4B\u79D1\u5B78\u53CA\u6280\u8853\u6A5F\u69CB\uFF08\u535A\u7269\u9928\u3001\u5716\u66F8\u9928\u3001\u6A94\u6848\u9928\u3001\u5BE6\u9A57\u5BA4\u3001\u5DE5\u4F5C\u5BA4\u2026\u2026\uFF09\uFF1B</li><li>\u7D44\u7E54\u5C0D\u8003\u53E4\u767C\u6398\u7684\u76E3\u7763\uFF0C\u78BA\u4FDD\u5728\u539F\u5730\u4FDD\u5B58\u67D0\u4E9B\u6587\u5316\u8CA1\u7522\uFF0C\u4E26\u4FDD\u8B77\u67D0\u4E9B\u5730\u5340\uFF0C\u4F9B\u4ECA\u5F8C\u8003\u53E4\u7814\u7A76\u4E4B\u7528\uFF1B</li><li>\u70BA\u6709\u95DC\u5404\u65B9\u9762\uFF08\u535A\u7269\u9928\u9577\u3001\u6536\u85CF\u5BB6\u3001\u53E4\u8463\u5546\u7B49\uFF09\u7684\u5229\u76CA\uFF0C\u5236\u8A02\u7B26\u5408\u65BC\u672C\u516C\u7D04\u6240\u898F\u5B9A\u9053\u5FB7\u539F\u5247\u7684\u898F\u7AE0\uFF1B\u4E26\u63A1\u53D6\u63AA\u65BD\u4FDD\u8B49\u9075\u5B88\u9019\u4E9B\u898F\u7AE0\uFF1B</li><li>\u63A1\u53D6\u6559\u80B2\u63AA\u65BD\uFF0C\u9F13\u52F5\u4E26\u63D0\u9AD8\u5C0D\u5404\u570B\u6587\u5316\u907A\u7522\u7684\u5C0A\u91CD\uFF0C\u4E26\u50B3\u64AD\u95DC\u65BC\u672C\u516C\u7D04\u898F\u5B9A\u7684\u77E5\u8B58\uFF1B</li><li>\u6CE8\u610F\u5C0D\u4EFB\u4F55\u7A2E\u985E\u7684\u6587\u5316\u8CA1\u7522\u7684\u5931\u8E64\u9032\u884C\u9069\u7576\u5BA3\u50B3\u3002</li></ol><h3 id="\u7B2C\u516D\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516D\u689D" aria-hidden="true">#</a> \u7B2C\u516D\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\uFF1A</p><ol><li>\u767C\u653E\u9069\u7576\u8B49\u4EF6\uFF0C\u51FA\u53E3\u570B\u5C07\u5728\u8A72\u8B49\u4EF6\u4E2D\u8AAA\u660E\u6709\u95DC\u6587\u5316\u8CA1\u7522\u7684\u51FA\u53E3\u5DF2\u7D93\u904E\u6279\u51C6\u3002\u6839\u64DA\u898F\u5B9A\u51FA\u53E3\u7684\u5404\u7A2E\u6587\u5316\u8CA1\u7522\uFF0C\u5747\u9808\u9644\u6709\u6B64\u7A2E\u8B49\u4EF6\uFF1B</li><li>\u9664\u975E\u9644\u6709\u4E0A\u8FF0\u51FA\u53E3\u8B49\u4EF6\uFF0C\u7981\u6B62\u6587\u5316\u8CA1\u7522\u5F9E\u672C\u570B\u9818\u571F\u51FA\u53E3\uFF1B</li><li>\u901A\u904E\u9069\u7576\u65B9\u6CD5\u5BA3\u50B3\u9019\u7A2E\u7981\u6B62\uFF0C\u7279\u5225\u8981\u5728\u53EF\u80FD\u62BD\u6216\u9032\u53E3\u6587\u5316\u8CA1\u7522\u7684\u4EBA\u5011\u4E2D\u9593\u9032\u884C\u5BA3\u50B3\u3002</li></ol><h3 id="\u7B2C\u4E03\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E03\u689D" aria-hidden="true">#</a> \u7B2C\u4E03\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\uFF1A</p><ol><li>\u63A1\u53D6\u8207\u672C\u570B\u7ACB\u6CD5\u76F8\u4E00\u81F4\u7684\u5FC5\u8981\u63AA\u65BD\u9632\u6B62\u672C\u570B\u9818\u571F\u5167\u7684\u535A\u7269\u9928\u53CA\u985E\u4F3C\u6A5F\u69CB\u7372\u53D6\u4F86\u6E90\u65BC\u53E6\u4E00\u7DE0\u7D04\u570B\u4E26\u65BC\u672C\u516C\u7D04\u5728\u6709\u95DC\u570B\u5BB6\u751F\u6548\u5F8C\u975E\u6CD5\u51FA\u53E3\u7684\u6587\u5316\u8CA1\u7522\u3002\u672C\u516C\u7D04\u5C0D\u5169\u570B\u5747\u4EE5\u751F\u6548\u5F8C\uFF0C\u76E1\u53EF\u80FD\u96A8\u6642\u628A\u81EA\u5169\u570B\u4E2D\u7684\u539F\u4E3B\u7DE0\u7D04\u570B\u975E\u6CD5\u904B\u51FA\u6587\u5316\u8CA1\u7522\u7684\u5EFA\u8B70\u901A\u77E5\u8A72\u539F\u4E3B\u7DE0\u7D04\u570B\u3002</li><li><ul><li>\u672C\u516C\u7D04\u5C0D\u6709\u95DC\u570B\u5BB6\u751F\u6548\u5F8C\uFF0C\u7981\u6B62\u9032\u53E3\u5F9E\u672C\u516C\u7D04\u53E6\u4E00\u7DE0\u7D04\u570B\u7684\u535A\u7269\u9928\u6216\u5B97\u6559\u7684\u6216\u4E16\u4FD7\u7684\u516C\u5171\u7D00\u5FF5\u9928\u6216\u985E\u4F3C\u6A5F\u69CB\u4E2D\u7ACA\u53D6\u7684\u6587\u5316\u8CA1\u7522\uFF0C\u5982\u679C\u8A72\u9805\u8CA1\u7522\u696D\u5DF2\u7528\u6587\u4EF6\u5F62\u5F0F\u5217\u5165\u8A72\u6A5F\u69CB\u7684\u8CA1\u7522\u6E05\u518A\uFF1B</li><li>\u672C\u516C\u7D04\u5C0D\u6709\u95DC\u5169\u500B\u570B\u5BB6\u751F\u6548\u5F8C\uFF0C\u6839\u64DA\u5169\u570B\u4E2D\u7684\u539F\u4E3B\u7DE0\u7D04\u570B\u7684\u8981\u6C42\uFF0C\u63A1\u53D6\u9069\u7576\u63AA\u65BD\u6536\u56DE\u4E26\u6B78\u9084\u9032\u53E3\u7684\u6B64\u985E\u6587\u5316\u8CA1\u7522\uFF0C\u4F46\u8981\u6C42\u570B\u9808\u5411\u4E0D\u77E5\u60C5\u7684\u8CB7\u4E3B\u6216\u5C0D\u8A72\u8CA1\u7522\u5177\u6709\u5408\u6CD5\u6B0A\u5229\u8005\u7D66\u4E88\u516C\u5E73\u7684\u8CE0\u511F\u3002\u8981\u6C42\u6536\u56DE\u548C\u6B78\u9084\u5931\u7269\u5FC5\u9808\u901A\u904E\u5916\u4EA4\u90E8\u9580\u9032\u884C\uFF0C\u63D0\u51FA\u8981\u6C42\u4E00\u65B9\u61C9\u63D0\u4F9B\u4F7F\u78BA\u5B9A\u5176\u6536\u56DE\u6216\u6B78\u9084\u5931\u7269\u7684\u8981\u6C42\u7684\u5FC5\u8981\u6587\u4EF6\u53CA\u5176\u4ED6\u8B49\u64DA\uFF0C\u8CBB\u7528\u81EA\u7406\u3002\u5404\u65B9\u4E0D\u5F97\u5C0D\u9075\u7167\u672C\u689D\u898F\u5B9A\u800C\u6B78\u9084\u7684\u6587\u5316\u8CA1\u7522\u5F81\u6536\u95DC\u7A05\u6216\u5176\u4ED6\u8CBB\u7528\u3002\u6B78\u9084\u548C\u904B\u9001\u6587\u5316\u8CA1\u7522\u904E\u7A0B\u4E2D\u6240\u9700\u7684\u4E00\u5207\u8CBB\u7528\u5747\u7531\u63D0\u51FA\u8981\u6C42\u4E00\u65B9\u8CA0\u64D4\u3002</li></ul></li></ol><h3 id="\u7B2C\u516B\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516B\u689D" aria-hidden="true">#</a> \u7B2C\u516B\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\u5C0D\u89F8\u72AF\u4E0A\u8FF0\u7B2C\u516D\u689D\uFF082\uFF09\u548C\u7B2C\u4E03\u689D\uFF082\uFF09\u6240\u5217\u7684\u7981\u6B62\u898F\u5B9A\u8CA0\u6709\u8CAC\u4EFB\u8005\u4E88\u4EE5\u61F2\u8655\u6216\u884C\u653F\u5236\u88C1\u3002</p><h3 id="\u7B2C\u4E5D\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E5D\u689D" aria-hidden="true">#</a> \u7B2C\u4E5D\u689D</h3><p>\u672C\u516C\u7D04\u7684\u4EFB\u4E00\u7DE0\u7D04\u570B\u5728\u5176\u6587\u5316\u907A\u7522\u7531\u65BC\u8003\u53E4\u6216\u4EBA\u7A2E\u5B78\u7684\u6750\u6599\u906D\u53D7\u63A0\u596A\u800C\u8655\u5883\u5371\u6B86\u6642\u5F97\u5411\u8499\u53D7\u5F71\u97FF\u7684\u5176\u4ED6\u7DE0\u7D04\u570B\u767C\u51FA\u547C\u7C72\u3002\u5728\u6B64\u60C5\u6CC1\u4E0B\uFF0C\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\u53C3\u8207\u5354\u8ABF\u4E00\u81F4\u7684\u570B\u969B\u52AA\u529B\uFF0C\u4EE5\u78BA\u5B9A\u4E26\u5BE6\u65BD\u5FC5\u8981\u7684\u5177\u9AD4\u63AA\u65BD\uFF0C\u5305\u62EC\u5C0D\u6709\u95DC\u7684\u7279\u5B9A\u7269\u8CC7\u7684\u9032\u51FA\u53E3\u53CA\u570B\u969B\u8CBF\u6613\u5BE6\u884C\u7BA1\u5236\u3002\u5728\u5C1A\u672A\u9054\u6210\u5354\u8B70\u4E4B\u524D\uFF0C\u6709\u95DC\u5404\u570B\u61C9\u5728\u53EF\u80FD\u7BC4\u570D\u5167\u63A1\u53D6\u81E8\u6642\u6027\u63AA\u65BD\uFF0C\u4EE5\u4FBF\u5236\u6B62\u5C0D\u63D0\u51FA\u8981\u6C42\u7684\u570B\u5BB6\u7684\u6587\u5316\u907A\u7522\u9020\u6210\u4E0D\u53EF\u5F4C\u88DC\u7684\u640D\u5931\u3002</p><h3 id="\u7B2C\u5341\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u64D4\uFF1A</p><ol><li>\u901A\u904E\u6559\u80B2\u3001\u60C5\u5831\u548C\u9632\u7BC4\u624B\u6BB5\uFF0C\u9650\u5236\u975E\u6CD5\u5F9E\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u904B\u51FA\u7684\u6587\u5316\u8CA1\u7522\u7684\u79FB\u52D5\uFF0C\u4E26\u8996\u5404\u570B\u60C5\u6CC1\uFF0C\u8CAC\u6210\u53E4\u8463\u5546\u4FDD\u6301\u4E00\u4EFD\u8A18\u9304\uFF0C\u8F09\u660E\u6BCF\u9805\u6587\u5316\u8CA1\u7522\u7684\u4F86\u6E90\u3001\u63D0\u4F9B\u8005\u7684\u59D3\u540D\u8207\u4F4F\u5740\u4EE5\u53CA\u6BCF\u9805\u552E\u51FA\u7684\u7269\u54C1\u8CA1\u7522\u7684\u8CFC\u8CB7\u4EBA\uFF0C\u9055\u8005\u9808\u53D7\u5211\u4E8B\u6216\u884C\u653F\u5236\u88C1\u3002</li><li>\u52AA\u529B\u901A\u904E\u6559\u80B2\u624B\u6BB5\uFF0C\u4F7F\u516C\u773E\u5FC3\u76EE\u4E2D\u8A8D\u8B58\u5230\uFF0C\u4E26\u9032\u4E00\u6B65\u7406\u89E3\u6587\u5316\u8CA1\u7522\u7684\u50F9\u503C\u548C\u5077\u76DC\u3001\u79D8\u5BC6\u767C\u6398\u8207\u975E\u6CD5\u51FA\u53E3\u5C0D\u6587\u5316\u8CA1\u7522\u9020\u6210\u7684\u5A01\u8105\u3002</li></ol><h3 id="\u7B2C\u5341\u4E00\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E00\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E00\u689D</h3><p>\u4E00\u500B\u570B\u5BB6\u76F4\u63A5\u6216\u9593\u63A5\u5730\u7531\u65BC\u88AB\u4ED6\u570B\u5360\u9818\u800C\u88AB\u8FEB\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u6216\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u61C9\u88AB\u8996\u70BA\u975E\u6CD5\u3002</p><h3 id="\u7B2C\u5341\u4E8C\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E8C\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E8C\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u61C9\u5C0A\u91CD\u7531\u5176\u8CA0\u8CAC\u570B\u969B\u95DC\u4FC2\u7684\u9818\u571F\u5185\u7684\u6587\u5316\u8CA1\u7522\uFF0C\u4E26\u61C9\u91C7\u53D6\u4E00\u5207\u9069\u7576\u63AA\u65BD\u7981\u6B62\u4E26\u9632\u6B62\u5728\u9019\u4E9B\u9818\u571F\u5185\u975E\u6CD5\u9032\u51FA\u53E3\u6587\u5316\u8CA1\u7522\u548C\u975E\u6CD5\u8F49\u8B93\u5176\u6240\u6709\u6B0A\u3002</p><h3 id="\u7B2C\u5341\u4E09\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E09\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E09\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u9084\u61C9\u5728\u7B26\u5408\u5176\u672C\u570B\u6CD5\u5F8B\u7684\u60C5\u6CC1\u4E0B\u627F\u64D4\uFF1A</p><ol><li>\u901A\u904E\u4E00\u5207\u9069\u7576\u624B\u6BB5\u9632\u6B62\u53EF\u80FD\u5F15\u8D77\u6587\u5316\u8CA1\u7522\u7684\u975E\u6CD5\u9032\u51FA\u53E3\u7684\u9019\u4E00\u985E\u8CA1\u7522\u7684\u6240\u6709\u6B0A\u8F49\u8B93\uFF1B</li><li>\u4FDD\u8B49\u672C\u570B\u7684\u4E3B\u7BA1\u6A5F\u95DC\u9032\u884C\u5408\u4F5C\uFF0C\u4F7F\u975E\u6CD5\u51FA\u53E3\u7684\u6587\u5316\u8CA1\u7522\u76E1\u65E9\u6B78\u9084\u5176\u5408\u6CD5\u6240\u6709\u8005\uFF1B</li><li>\u53D7\u7406\u5408\u6CD5\u6240\u6709\u8005\u6216\u5176\u4EE3\u8868\u63D0\u51FA\u7684\u95DC\u65BC\u627E\u56DE\u5931\u843D\u7684\u6216\u5931\u7ACA\u7684\u6587\u5316\u8CA1\u7522\u7684\u8A34\u8A1F\uFF1B</li><li>\u627F\u8A8D\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u6709\u4E0D\u53EF\u53D6\u6D88\u7684\u6B0A\u5229\u898F\u5B9A\u4E26\u5BA3\u4F48\u67D0\u4E9B\u6587\u5316\u8CA1\u7522\u662F\u4E0D\u80FD\u8B93\u8207\u7684\uFF0C\u56E0\u800C\u64DA\u6B64\u4E5F\u4E0D\u80FD\u51FA\u53E3\uFF0C\u82E5\u6B64\u985E\u8CA1\u7522\u5DF2\u7D93\u51FA\u53E3\u52D9\u9808\u4FC3\u4F7F\u5C07\u9019\u985E\u8CA1\u7522\u6B78\u9084\u7D66\u6709\u95DC\u570B\u5BB6\u3002</li></ol><h3 id="\u7B2C\u5341\u56DB\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u56DB\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u56DB\u689D</h3><p>\u70BA\u9632\u6B62\u975E\u6CD5\u51FA\u53E3\u3001\u5C65\u884C\u672C\u516C\u7D04\u6240\u898F\u5B9A\u7684\u7FA9\u52D9\uFF0C\u672C\u516C\u7D04\u5404\u7DE0\u7D04\u570B\u61C9\u5728\u53EF\u80FD\u7BC4\u570D\u5167\u70BA\u5176\u8CA0\u8CAC\u4FDD\u8B77\u6587\u5316\u907A\u7522\u7684\u570B\u5BB6\u6A5F\u95DC\u63D0\u4F9B\u8DB3\u5920\u7684\u9810\u7B97\u4E26\u5728\u5FC5\u8981\u6642\u70BA\u6B64\u76EE\u7684\u8A2D\u7ACB\u4E00\u9805\u57FA\u91D1\u3002</p><h3 id="\u7B2C\u5341\u4E94\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E94\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E94\u689D</h3><p>\u5728\u672C\u516C\u7D04\u5C0D\u6709\u95DC\u570B\u5BB6\u751F\u6548\u524D\uFF0C\u672C\u516C\u7D04\u4E4B\u4EFB\u4F55\u898F\u5B9A\u4E0D\u61C9\u59A8\u7919\u7DE0\u7D04\u570B\u4E4B\u9593\u81EA\u884C\u7DE0\u7D50\u6709\u95DC\u6B78\u9084\u5F9E\u5176\u539F\u4E3B\u570B\u9818\u571F\u4E0A\u4E0D\u8AD6\u4EE5\u4F55\u7A2E\u7406\u7531\u642C\u8D70\u4E4B\u6587\u5316\u8CA1\u7522\u7684\u7279\u5225\u5354\u5B9A\uFF0C\u6216\u5236\u6B62\u5B83\u5011\u7E7C\u7E8C\u57F7\u884C\u696D\u5DF2\u7DE0\u7D50\u7684\u6709\u95DC\u5354\u5B9A\u3002</p><h3 id="\u7B2C\u5341\u516D\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u516D\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u516D\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u61C9\u5728\u5411\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u5927\u6703\u63D0\u4EA4\u7684\u5B9A\u671F\u5831\u544A\u4E2D\uFF0C\u63D0\u4F9B\u5B83\u5011\u5DF2\u7D93\u901A\u904E\u7684\u7ACB\u6CD5\u548C\u884C\u653F\u898F\u5B9A\u548C\u5B83\u5011\u70BA\u5BE6\u65BD\u672C\u516C\u7D04\u6240\u91C7\u53D6\u7684\u5176\u4ED6\u884C\u52D5\u4EE5\u53CA\u5728\u6B64\u9818\u57DF\u5185\u53D6\u5F97\u7684\u8A73\u76E1\u7D93\u9A57\u7684\u8CC7\u6599\uFF0C\u5831\u544A\u7684\u65E5\u671F\u53CA\u65B9\u5F0F\u7531\u5927\u6703\u6C7A\u5B9A\u3002</p><h3 id="\u7B2C\u5341\u4E03\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E03\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E03\u689D</h3><ol><li>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u53EF\u4EE5\u5411\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u8ACB\u6C42\u7D66\u4E88\u6280\u8853\u63F4\u52A9\uFF0C\u7279\u5225\u662F\u6709\u95DC\uFF1A <ul><li>\u60C5\u5831\u548C\u6559\u80B2\uFF1B</li><li>\u54A8\u8A62\u548C\u5C08\u5BB6\u5EFA\u8B70\uFF1B</li><li>\u5354\u8ABF\u548C\u65A1\u65CB\u3002</li></ul></li><li>\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u53EF\u4EE5\u4E3B\u52D5\u9032\u884C\u6709\u95DC\u975E\u6CD5\u8F49\u79FB\u6587\u5316\u8CA1\u7522\u554F\u984C\u7684\u7814\u7A76\u4E26\u51FA\u7248\u7814\u7A76\u5831\u544A\u3002</li><li>\u70BA\u6B64\uFF0C\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u53EF\u4EE5\u8ACB\u6C42\u4EFB\u4F55\u975E\u653F\u5E9C\u7684\u4E3B\u7BA1\u7D44\u7E54\u4E88\u4EE5\u5408\u4F5C\u3002</li><li>\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u53EF\u4EE5\u4E3B\u52D5\u5411\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u63D0\u51FA\u6709\u95DC\u672C\u516C\u7D04\u7684\u5BE6\u65BD\u7684\u5EFA\u8B70\u3002</li><li>\u7D93\u904E\u672C\u516C\u7D04\u7684\u5BE6\u65BD\u6709\u722D\u8B70\u7684\u5169\u500B\u4EE5\u4E0A\u7684\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u7684\u8ACB\u6C42\uFF0C\u806F\u5408\u570B\u6559\u79D1\u6587\u7D44\u7E54\u5F97\u9032\u884C\u65A1\u65CB\uFF0C\u4F7F\u5B83\u5011\u4E4B\u9593\u7684\u722D\u7AEF\u5F97\u5230\u89E3\u6C7A\u3002</li></ol><h3 id="\u7B2C\u5341\u516B\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u516B\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u516B\u689D</h3><p>\u672C\u516C\u7D04\u4EE5\u82F1\u6587\u3001\u6CD5\u6587\u3001\u4FC4\u6587\u548C\u897F\u73ED\u7259\u6587\u5236\u5B9A\uFF0C\u56DB\u7A2E\u6587\u672C\u5177\u6709\u540C\u7B49\u6548\u529B\u3002</p><h3 id="\u7B2C\u5341\u4E5D\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E5D\u689D" aria-hidden="true">#</a> \u7B2C\u5341\u4E5D\u689D</h3><ol><li>\u672C\u516C\u7D04\u9808\u7D93\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u6703\u54E1\u570B\u6309\u5404\u570B\u61B2\u6CD5\u7A0B\u5E8F\u6279\u51C6\u6216\u63A5\u53D7\u3002</li><li>\u6279\u51C6\u66F8\u6216\u63A5\u53D7\u66F8\uFF0C\u61C9\u4EA4\u5B58\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u3002</li></ol><h3 id="\u7B2C\u4E8C\u5341\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u689D</h3><ol><li>\u672C\u516C\u7D04\u61C9\u958B\u653E\u7D66\u975E\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u6210\u54E1\u4F46\u7D93\u672C\u7D44\u7E54\u57F7\u884C\u5C40\u9080\u8ACB\u52A0\u5165\u672C\u516C\u7D04\u7684\u6240\u6709\u570B\u5BB6\u52A0\u5165\u3002</li><li>\u52A0\u5165\u66F8\u4EA4\u5B58\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u5F8C\uFF0C\u52A0\u5165\u5373\u884C\u751F\u6548\u3002</li></ol><h3 id="\u7B2C\u4E8C\u5341\u4E00\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E00\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E00\u689D</h3><p>\u672C\u516C\u7D04\u5728\u6536\u5230\u7B2C3\u4EFD\u6279\u51C6\u66F8\u3001\u63A5\u53D7\u66F8\u6216\u52A0\u5165\u66F8\u5F8C\u7684\u4E09\u500B\u6708\u958B\u59CB\u751F\u6548\uFF0C\u4F46\u9019\u652F\u968A\u90A3\u4E9B\u5728\u8A72\u65E5\u6216\u8A72\u65E5\u4E4B\u524D\u696D\u5DF2\u4EA4\u5B58\u5176\u5404\u81EA\u7684\u6279\u51C6\u66F8\u3001\u63A5\u53D7\u66F8\u6216\u52A0\u5165\u66F8\u7684\u570B\u5BB6\u751F\u6548\u3002\u5C0D\u65BC\u4EFB\u4F55\u5176\u4ED6\u570B\u5BB6\uFF0C\u672C\u516C\u7D04\u5247\u5728\u5176\u6279\u51C6\u66F8\u3001\u63A5\u53D7\u66F8\u6216\u52A0\u5165\u66F8\u4EA4\u5B58\u5F8C\u4E09\u500B\u6708\u958B\u59CB\u751F\u6548\u3002</p><h3 id="\u7B2C\u4E8C\u5341\u4E8C\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E8C\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E8C\u689D</h3><p>\u672C\u516C\u7D04\u7DE0\u7D04\u570B\u627F\u8A8D\uFF0C\u672C\u516C\u7D04\u4E0D\u50C5\u4F7F\u7528\u65BC\u5176\u672C\u570B\u9818\u571F\uFF0C\u800C\u4E14\u4E5F\u9069\u7528\u65BC\u5728\u570B\u969B\u95DC\u4FC2\u4E0A\u7531\u5176\u8CA0\u8CAC\u7684\u4E00\u5207\u9818\u571F\uFF1B\u5982\u6709\u5FC5\u8981\uFF0C\u7DE0\u7D04\u570B\u9808\u5728\u6279\u51C6\u3001\u63A5\u53D7\u6216\u52A0\u5165\u4E4B\u6642\u6216\u4EE5\u524D\u8207\u9019\u4E9B\u9818\u571F\u7684\u653F\u5E9C\u6216\u5176\u4ED6\u4E3B\u7BA1\u7576\u5C40\u9032\u884C\u78CB\u5546\uFF0C\u4EE5\u4FBF\u4FDD\u8B49\u672C\u516C\u7D04\u5728\u9019\u4E9B\u9818\u571F\u7684\u9069\u7528\uFF0C\u4E26\u5C07\u672C\u516C\u7D04\u9069\u7528\u7684\u9818\u571F\u901A\u77E5\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\uFF0C\u8A72\u901A\u77E5\u5728\u6536\u5230\u4E4B\u65E5\u8D773\u500B\u6708\u751F\u6548\u3002</p><h3 id="\u7B2C\u4E8C\u5341\u4E09\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E09\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E09\u689D</h3><ol><li>\u672C\u516C\u7D04\u4E4B\u6BCF\u4E00\u7DE0\u7D04\u570B\u53EF\u4EE5\u4EE3\u8868\u672C\u570B\u6216\u4EE3\u8868\u7531\u5176\u8CA0\u8CAC\u570B\u969B\u95DC\u4FC2\u7684\u4EFB\u4F55\u9818\u571F\u9000\u51FA\u672C\u516C\u7D04\u3002</li><li>\u9000\u7D04\u9808\u4EE5\u66F8\u9762\u6587\u4EF6\u901A\u77E5\uFF0C\u8A72\u9000\u7D04\u66F8\u4EA4\u5B58\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u8655\u3002</li><li>\u9000\u7D04\u5728\u6536\u5230\u9000\u7D04\u901A\u77E5\u66F8\u5F8C12\u500B\u6708\u751F\u6548\u3002</li></ol><h3 id="\u7B2C\u4E8C\u5341\u56DB\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u56DB\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u56DB\u689D</h3><p>\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u9808\u5C07\u7B2C\u5341\u4E5D\u689D\u548C\u4E8C\u5341\u689D\u4E2D\u898F\u5B9A\u7684\u6709\u95DC\u6279\u51C6\u66F8\u3001\u63A5\u53D7\u66F8\u548C\u52A0\u5165\u66F8\u7684\u4EA4\u5B58\u60C5\u6CC1\u4EE5\u53CA\u7B2C\u4E8C\u5341\u4E8C\u689D\u548C\u7B2C\u4E8C\u5341\u4E09\u689D\u5206\u5225\u898F\u5B9A\u7684\u901A\u77E5\u548C\u9000\u7D04\u544A\u77E5\u672C\u7D44\u7E54\u6703\u54E1\u570B\u3001\u7B2C\u4E8C\u5341\u689D\u4E2D\u6240\u5C6C\u7684\u975E\u672C\u7D44\u7E54\u6703\u54E1\u7684\u570B\u5BB6\u4EE5\u53CA\u806F\u5408\u570B\u3002</p><h3 id="\u7B2C\u4E8C\u5341\u4E94\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E94\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E94\u689D</h3><ol><li>\u672C\u516C\u7D04\u53EF\u7D93\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u5927\u6703</li><li>\u4E88\u4EE5\u4FEE\u6B63\u3002\u4EFB\u4F55\u9019\u6A23\u7684\u4FEE\u6B63\u53EA\u5C0D\u4FEE\u6B63\u516C\u7D04\u7684\u7DE0\u7D04\u570B\u5177\u6709\u7D04\u675F\u529B\u3002</li></ol><p>\u5982\u5927\u6703\u901A\u904E\u4E00\u9805\u5168\u9762\u6216\u90E8\u5206\u5730\u4FEE\u8A02\u672C\u516C\u7D04\u7684\u65B0\u516C\u7D04\uFF0C\u5247\u9664\u975E\u65B0\u516C\u7D04\u53E6\u6709\u898F\u5B9A\uFF0C\u672C\u516C\u7D04\u5728\u65B0\u7684\u4FEE\u8A02\u516C\u7D04\u751F\u6548\u4E4B\u65E5\u8D77\u505C\u6B62\u4E00\u5207\u6279\u51C6\u3001\u63A5\u53D7\u6216\u52A0\u5165\u3002</p><h3 id="\u7B2C\u4E8C\u5341\u516D\u689D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u516D\u689D" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u516D\u689D</h3><p>\u7D93\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u7684\u8981\u6C42\uFF0C\u672C\u516C\u7D04\u61C9\u6309\u7167\u300A\u806F\u5408\u570B\u61B2\u7AE0\u300B\u7B2C\u4E00\u767E\u96F6\u4E8C\u689D\u7684\u898F\u5B9A\u5728\u806F\u5408\u570B\u79D8\u66F8\u8655\u7B49\u7D1A\u3002</p><p>1970\u5E7411\u670817\u65E5\u8A02\u65BC\u5DF4\u9ECE\u3002\u5169\u500B\u6B63\u5F0F\u6587\u672C\u5747\u6709\u5927\u6703\u7B2C\u5341\u516D\u5C46\u6703\u8B70\u4E3B\u5E2D\u548C\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7E3D\u5E79\u4E8B\u7684\u7C3D\u540D\uFF0C\u5C07\u4EA4\u5B58\u65BC\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u7684\u6A94\u6848\u5EAB\u4E2D\u3002\u9A57\u8B49\u7121\u8AA4\u4E4B\u526F\u672C\u5C07\u5206\u9001\u7B2C\u5341\u4E5D\u689D\u5230\u4E8C\u5341\u689D\u6240\u8FF0\u4E4B\u6240\u6709\u570B\u5BB6\u548C\u806F\u5408\u570B\u3002</p><p>\u4EE5\u4E0A\u4E431970\u5E7411\u670814\u65E5\u5728\u5DF4\u9ECE\u53EC\u958B\u7684\u806F\u5408\u570B\u6559\u80B2\u3001\u79D1\u5B78\u53CA\u6587\u5316\u7D44\u7E54\u5927\u6703\u7B2C\u5341\u516D\u5C46\u6703\u8B70\u6B63\u5F0F\u901A\u904E\u4E4B\u516C\u7D04\u7684\u4F5C\u51C6\u6587\u672C\u3002</p><p>\u6211\u5011\u65BC1970\u5E7411\u670817\u65E5\u7C3D\u5B57\uFF0C\u4EE5\u662D\u4FE1\u5B88\u3002</p><p align="center">\u5927\u6703\u4E3B\u5E2D</p><p align="center">\u963F\u8482\u5229\u5967\uFF0E\u5FB7\u7D61\u7F85\uFF0E\u99AC\u4F0A\u5C3C</p><p align="center">\u7E3D\u5E79\u4E8B</p><p align="center">\u52D2\u5185\uFF0E\u99AC\u5384</p><h2 id="convention-on-the-means-of-prohibiting-and-preventing-the-illicit-import-export-and-transfer-of-ownership-of-cultural-property" tabindex="-1"><a class="header-anchor" href="#convention-on-the-means-of-prohibiting-and-preventing-the-illicit-import-export-and-transfer-of-ownership-of-cultural-property" aria-hidden="true">#</a> Convention on the Means of Prohibiting and Preventing the Illicit Import, Export and Transfer of Ownership of Cultural Property</h2><p>The General Conference of the United Nations Educational, Scientific and Cultural Organization, meeting in Paris from 12 October to 14 November 1970, at its sixteenth session,</p><p>Recalling the importance of the provisions contained in the Declaration of the Principles of International Cultural Co-operation, adopted by the General Conference at its fourteenth session,</p><p>Considering that the interchange of cultural property among nations for scientific, cultural and educational purposes increases the knowledge of the civilization of Man, enriches the cultural life of all peoples and inspires mutual respect and appreciation among nations,</p><p>Considering that cultural property constitutes one of the basic elements of civilization and national culture, and that its true value can be appreciated only in relation to the fullest possible information regarding its origin, history and traditional setting,</p><p>Considering that it is incumbent upon every State to protect the cultural property existing within its territory against the dangers of theft, clandestine excavation, and illicit export,</p><p>Considering that, to avert these dangers, it is essential for every State to become increasingly alive to the moral obligations to respect its own cultural heritage and that of all nations,</p><p>Considering that, as cultural institutions, museums, libraries and archives should ensure that their collections are built up in accordance with universally recognized moral principles,</p><p>Considering that the illicit import, export and transfer of ownership of cultural property is an obstacle to that understanding between nations which it is part of UNESCO\u2019s mission to promote by recommending to interested States, international conventions to this end,</p><p>Considering that the protection of cultural heritage can be effective only if organized both nationally and internationally among States working in close co-operation,</p><p>Considering that the UNESCO General Conference adopted a Recommendation to this effect in 1964,</p><p>Having before it further proposals on the means of prohibiting and preventing the illicit import, export and transfer of ownership of cultural property, a question which is on the agenda for the session as item 19,</p><p>Having decided, at its fifteenth session, that this question should be made the subject of an international convention,</p><p>Adopts this Convention on the fourteenth day of November 1970.</p><h3 id="article-1" tabindex="-1"><a class="header-anchor" href="#article-1" aria-hidden="true">#</a> Article 1</h3><p>For the purposes of this Convention, the term `cultural property\u2019 means property which, on religious or secular grounds, is specifically designated by each State as being of importance for archaeology, prehistory, history, literature, art or science and which belongs to the following categories:</p><ul><li>(a) Rare collections and specimens of fauna, flora, minerals and anatomy, and objects of paleontological interest;</li><li>(b) property relating to history, including the history of science and technology and military and social history, to the life of national leaders, thinkers, scientists and artist and to events of national importance;</li><li>(c) products of archaeological excavations (including regular and clandestine) or of archaeological discoveries ;</li><li>(d) elements of artistic or historical monuments or archaeological sites which have been dismembered;</li><li>(e) antiquities more than one hundred years old, such as inscriptions, coins and engraved seals;</li><li>(f) objects of ethnological interest;</li><li>(g) property of artistic interest, such as: <ul><li>(i) pictures, paintings and drawings produced entirely by hand on any support and in any material (excluding industrial designs and manufactured articles decorated by hand);</li><li>(ii) original works of statuary art and sculpture in any material;</li><li>(iii) original engravings, prints and lithographs;</li><li>(iv) original artistic assemblages and montages in any material;</li></ul></li><li>(h) rare manuscripts and incunabula, old books, documents and publications of special interest (historical, artistic, scientific, literary, etc.) singly or in collections ;</li><li>(i) postage, revenue and similar stamps, singly or in collections;</li><li>(j) archives, including sound, photographic and cinematographic archives;</li><li>(k) articles of furniture more than one hundred years old and old musical instruments.</li></ul><h3 id="article-2" tabindex="-1"><a class="header-anchor" href="#article-2" aria-hidden="true">#</a> Article 2</h3><p>The States Parties to this Convention recognize that the illicit import, export and transfer of ownership of cultural property is one of the main causes of the impoverishment of the cultural heritage of the countries of origin of such property and that international co-operation constitutes one of the most efficient means of protecting each country\u2019s cultural property against all the dangers resulting there from.</p><p>To this end, the States Parties undertake to oppose such practices with the means at their disposal, and particularly by removing their causes, putting a stop to current practices, and by helping to make the necessary reparations.</p><h3 id="article-3" tabindex="-1"><a class="header-anchor" href="#article-3" aria-hidden="true">#</a> Article 3</h3><p>The import, export or transfer of ownership of cultural property effected contrary to the provisions adopted under this Convention by the States Parties thereto, shall be illicit.</p><h3 id="article-4" tabindex="-1"><a class="header-anchor" href="#article-4" aria-hidden="true">#</a> Article 4</h3><p>The States Parties to this Convention recognize that for the purpose of the Convention property which belongs to the following categories forms part of the cultural heritage of each State:</p><ul><li>(a) Cultural property created by the individual or collective genius of nationals of the State concerned, and cultural property of importance to the State concerned created within the territory of that State by foreign nationals or stateless persons resident within such territory;</li><li>(b) cultural property found within the national territory;</li><li>(c) cultural property acquired by archaeological, ethnological or natural science missions, with the consent of the competent authorities of the country of origin of such property;</li><li>(d) cultural property which has been the subject of a freely agreed exchange;</li><li>(e) cultural property received as a gift or purchased legally with the consent of the competent authorities of the country of origin of such property.</li></ul><h3 id="article-5" tabindex="-1"><a class="header-anchor" href="#article-5" aria-hidden="true">#</a> Article 5</h3><p>To ensure the protection of their cultural property against illicit import, export and transfer of ownership, the States Parties to this Convention undertake, as appropriate for each country, to set up within their territories one or more national services, where such services do not already exist, for the protection of the cultural heritage, with a qualified staff sufficient in number for the effective carrying out of the following functions:</p><ul><li>(a) contributing to the formation of draft laws and regulations designed to secure the protection of the cultural heritage and particularly prevention of the illicit import, export and transfer of ownership of important cultural property;</li><li>(b) establishing and keeping up to date, on the basis of a national inventory of protected property, a list of important public and private cultural property whose export would constitute an appreciable impoverishment of the national cultural heritage;</li><li>(c) promoting the development or the establishment of scientific and technical institutions (museums, libraries, archives, laboratories, workshops\u2026) required to ensure the preservation and presentation of cultural property;</li><li>(d) organizing the supervision of archaeological excavations, ensuring the preservation \u201Cin situ\u201D of certain cultural property, and protecting certain areas reserved for future archaeological research;</li><li>(e) establishing, for the benefit of those concerned (curators, collectors, antique dealers, etc.) rules in conformity with the ethical principles set forth in this Convention; and taking steps to ensure the observance of those rules;</li><li>(f) taking educational measures to stimulate and develop respect for the cultural heritage of all States, and spreading knowledge of the provisions of this Convention;</li><li>(g) seeing that appropriate publicity is given to the disappearance of any items of cultural property.</li></ul><h3 id="article-6" tabindex="-1"><a class="header-anchor" href="#article-6" aria-hidden="true">#</a> Article 6</h3><p>The States Parties to this Convention undertake:</p><ul><li>(a) To introduce an appropriate certificate in which the exporting State would specify that the export of the cultural property in question is authorized. The certificate should accompany all items of cultural property exported in accordance with the regulations;</li><li>(b) to prohibit the exportation of cultural property from their territory unless accompanied by the above-mentioned export certificate;</li><li>(c) to publicize this prohibition by appropriate means, particularly among persons likely to export or import cultural property.</li></ul><h3 id="article-7" tabindex="-1"><a class="header-anchor" href="#article-7" aria-hidden="true">#</a> Article 7</h3><p>The States Parties to this Convention undertake:</p><ul><li><p>(a) To take the necessary measures, consistent with national legislation, to prevent museums and similar institutions within their territories from acquiring cultural property originating in another State Party which has been illegally exported after entry into force of this Convention, in the States concerned. Whenever possible, to inform a State of origin Party to this Convention of an offer of such cultural property illegally removed from that State after the entry into force of this Convention in both States;</p></li><li><p>(b)</p><ul><li>(i) to prohibit the import of cultural property stolen from a museum or a religious or secular public monument or similar institution in another State Party to this Convention after the entry into force of this Convention for the States concerned, provided that such property is documented as appertaining to the inventory of that institution;</li><li>(ii) at the request of the State Party of origin, to take appropriate steps to recover and return any such cultural property imported after the entry into force of this Convention in both States concerned, provided, however, that the requesting State shall pay just compensation to an innocent purchaser or to a person who has valid title to that property. Requests for recovery and return shall be made through diplomatic offices. The requesting Party shall furnish, at its expense, the documentation and other evidence necessary to establish its claim for recovery and return. The Parties shall impose no customs duties or other charges upon cultural property returned pursuant to this Article. All expenses incident to the return and delivery of the cultural property shall be borne by the requesting Party.</li></ul></li></ul><h3 id="article-8" tabindex="-1"><a class="header-anchor" href="#article-8" aria-hidden="true">#</a> Article 8</h3><p>The States Parties to this Convention undertake to impose penalties or administrative sanctions on any person responsible for infringing the prohibitions referred to under Articles 6 (b) and 7 (b) above.</p><h3 id="article-9" tabindex="-1"><a class="header-anchor" href="#article-9" aria-hidden="true">#</a> Article 9</h3><p>Any State Party to this Convention whose cultural patrimony is in jeopardy from pillage of archaeological or ethnological materials may call upon other States Parties who are affected. The States Parties to this Convention undertake, in these circumstances, to participate in a concerted international effort to determine and to carry out the necessary concrete measures, including the control of exports and imports and international commerce in the specific materials concerned. Pending agreement each State concerned shall take provisional measures to the extent feasible to prevent irremediable injury to the cultural heritage of the requesting State.</p><h3 id="article-10" tabindex="-1"><a class="header-anchor" href="#article-10" aria-hidden="true">#</a> Article 10</h3><p>The States Parties to this Convention undertake:</p><ul><li>(a) To restrict by education, information and vigilance, movement of cultural property illegally removed from any State Party to this Convention and, as appropriate for each country, oblige antique dealers, subject to penal or administrative sanctions, to maintain a register recording the origin of each item of cultural property, names and addresses of the supplier, description and price of each item sold and to inform the purchaser of the cultural property of the export prohibition to which such property may be subject;</li><li>(b) to endeavour by educational means to create and develop in the public mind a realization of the value of cultural property and the threat to the cultural heritage created by theft, clandestine excavations and illicit exports.</li></ul><h3 id="article-11" tabindex="-1"><a class="header-anchor" href="#article-11" aria-hidden="true">#</a> Article 11</h3><p>The export and transfer of ownership of cultural property under compulsion arising directly or indirectly from the occupation of a country by a foreign power shall be regarded as illicit.</p><h3 id="article-12" tabindex="-1"><a class="header-anchor" href="#article-12" aria-hidden="true">#</a> Article 12</h3><p>The States Parties to this Convention shall respect the cultural heritage within the territories for the international relations of which they are responsible, and shall take all appropriate measures to prohibit and prevent the illicit import, export and transfer of ownership of cultural property in such territories.</p><h3 id="article-13" tabindex="-1"><a class="header-anchor" href="#article-13" aria-hidden="true">#</a> Article 13</h3><p>The States Parties to this Convention also undertake, consistent with the laws of each State:</p><ul><li>(a) To prevent by all appropriate means transfers of ownership of cultural property likely to promote the illicit import or export of such property;</li><li>(b) to ensure that their competent services co-operate in facilitating the earliest possible restitution of illicitly exported cultural property to its rightful owner;</li><li>(c) to admit actions for recovery of lost or stolen items of cultural property brought by or on behalf of the rightful owners;</li><li>(d) to recognize the indefeasible right of each State Party to this Convention to classify and declare certain cultural property as inalienable which should therefore ipso facto not be exported, and to facilitate recovery of such property by the State concerned in cases where it has been exported.</li></ul><h3 id="article-14" tabindex="-1"><a class="header-anchor" href="#article-14" aria-hidden="true">#</a> Article 14</h3><p>In order to prevent illicit export and to meet the obligations arising from the implementation of this Convention, each State Party to the Convention should, as far as it is able, provide the national services responsible for the protection of its cultural heritage with an adequate budget and, if necessary, should set up a fund for this purpose.</p><h3 id="article-15" tabindex="-1"><a class="header-anchor" href="#article-15" aria-hidden="true">#</a> Article 15</h3><p>Nothing in this Convention shall prevent States Parties thereto from concluding special agreements among themselves or from continuing to implement agreements already concluded regarding the restitution of cultural property removed, whatever the reason, from its territory of origin, before the entry into force of this Convention for the States concerned.</p><h3 id="article-16" tabindex="-1"><a class="header-anchor" href="#article-16" aria-hidden="true">#</a> Article 16</h3><p>The States Parties to this Convention shall in their periodic reports submitted to the General Conference of the United Nations Educational, Scientific and Cultural Organization on dates and in a manner to be determined by it, give information on the legislative and administrative provisions which they have adopted and other action which they have taken for the application of this Convention, together with details of the experience acquired in this field.</p><h3 id="article-17" tabindex="-1"><a class="header-anchor" href="#article-17" aria-hidden="true">#</a> Article 17</h3><p>The States Parties to this Convention may call on the technical assistance of the United Nations Educational, Scientific and Cultural Organization, particularly as regards:</p><ul><li>(a) Information and education;</li><li>(b) consultation and expert advice;</li><li>(c) co-ordination and good offices.</li></ul><p>The United Nations Educational, Scientific and Cultural Organization may, on its own initiative conduct research and publish studies on matters relevant to the illicit movement of cultural property.</p><p>To this end, the United Nations Educational, Scientific and Cultural Organization may also call on the co-operation of any competent non-governmental organization.</p><p>The United Nations Educational, Scientific and Cultural Organization may, on its own initiative, make proposals to States Parties to this Convention for its implementation.</p><p>At the request of at least two States Parties to this Convention which are engaged in a dispute over its implementation, UNESCO may extend its good offices to reach a settlement between them.</p><h3 id="article-18" tabindex="-1"><a class="header-anchor" href="#article-18" aria-hidden="true">#</a> Article 18</h3><p>This Convention is drawn up in English, French, Russian and Spanish, the four texts being equally authoritative.</p><h3 id="article-19" tabindex="-1"><a class="header-anchor" href="#article-19" aria-hidden="true">#</a> Article 19</h3><p>This Convention shall be subject to ratification or acceptance by States members of the United Nations Educational, Scientific and Cultural Organization in accordance with their respective constitutional procedures.</p><p>The instruments of ratification or acceptance shall be deposited with the Director-General of the United Nations Educational, Scientific and Cultural Organization.</p><h3 id="article-20" tabindex="-1"><a class="header-anchor" href="#article-20" aria-hidden="true">#</a> Article 20</h3><p>This Convention shall be open to accession by all States not members of the United Nations Educational, Scientific and Cultural Organization which are invited to accede to it by the Executive Board of the Organization.</p><p>Accession shall be effected by the deposit of an instrument of accession with the Director-General of the United Nations Educational, Scientific and Cultural Organization.</p><h3 id="article-21" tabindex="-1"><a class="header-anchor" href="#article-21" aria-hidden="true">#</a> Article 21</h3><p>This Convention shall enter into force three months after the date of the deposit of the third instrument of ratification, acceptance or accession, but only with respect to those States which have deposited their respective instruments on or before that date. It shall enter into force with respect to any other State three months after the deposit of its instrument of ratification, acceptance or accession.</p><h3 id="article-22" tabindex="-1"><a class="header-anchor" href="#article-22" aria-hidden="true">#</a> Article 22</h3><p>The States Parties to this Convention recognize that the Convention is applicable not only to their metropolitan territories but also to all territories for the international relations of which they are responsible; they undertake to consult, if necessary, the governments or other competent authorities of these territories on or before ratification, acceptance or accession with a view to securing the application of the Convention to those territories, and to notify the Director-General of the United Nations Educational, Scientific and Cultural Organization of the territories to which it is applied, the notification to take effect three months after the date of its receipt.</p><h3 id="article-23" tabindex="-1"><a class="header-anchor" href="#article-23" aria-hidden="true">#</a> Article 23</h3><p>Each State Party to this Convention may denounce the Convention on its own behalf or on behalf of any territory for whose international relations it is responsible.</p><p>The denunciation shall be notified by an instrument in writing, deposited with the Director-General of the United Nations Educational, Scientific and Cultural Organization.</p><p>The denunciation shall take effect twelve months after the receipt of the instrument of denunciation.</p><h3 id="article-24" tabindex="-1"><a class="header-anchor" href="#article-24" aria-hidden="true">#</a> Article 24</h3><p>The Director-General of the United Nations Educational, Scientific and Cultural Organization shall inform the States members of the Organization, the States not members of the Organization which are referred to in Article 20, as well as the United Nations, of the deposit of all the instruments of ratification, acceptance and accession provided for in Articles 19 and 20, and of the notifications and denunciations provided for in Articles 22 and 23 respectively.</p><h3 id="article-25" tabindex="-1"><a class="header-anchor" href="#article-25" aria-hidden="true">#</a> Article 25</h3><p>This Convention may be revised by the General Conference of the United Nations Educational, Scientific and Cultural Organization. Any such revision shall, however, bind only the States which shall become Parties to the revising convention.</p><p>If the General Conference should adopt a new convention revising this Convention in whole or in part, then, unless the new convention otherwise provides, this Convention shall cease to be open to ratification, acceptance or accession, as from the date on which the new revising convention enters into force.</p><h3 id="article-26" tabindex="-1"><a class="header-anchor" href="#article-26" aria-hidden="true">#</a> Article 26</h3><p>In conformity with ### Article 102 of the Charter of the United Nations, this Convention shall be registered with the Secretariat of the United Nations at the request of the Director-General of the United Nations Educational, Scientific and Cultural Organization.</p><p>Done in Paris this seventeenth day of November 1970, in two authentic copies bearing the signature of the President of the sixteenth session of the General Conference and of the Director-General of the United Nations Educational, Scientific and Cultural Organization, which shall be deposited in the archives of the United Nations Educational, Scientific and Cultural Organization, and certified true copies of which shall be delivered to all the States referred to in Articles 19 and 20 as well as to the United Nations.</p><p>The President of the General Conference ATILIO DELL\u2019ORO MAINI</p><p>The Director-General RENE MAHEU</p>',171);function r(n,o){return a}var h=e(i,[["render",r],["__file","1970-convention-on-the-means-of-prohibiting-and-preventing-the-illicit-import-export-and-transfer-of-ownership-of-cultural-property.html.vue"]]);export{h as default};
