import{r as t,o as n,c as r,a as e,e as s,F as o,b as a,d as c}from"./app.318fbbbd.js";import{_ as h}from"./plugin-vue_export-helper.21dcd24c.js";const d={},l=e("h1",{id:"international-council-of-museums-icom-statutes",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#international-council-of-museums-icom-statutes","aria-hidden":"true"},"#"),a(" International Council of Museums (ICOM) - Statutes")],-1),u={href:"https://icommuseum/wp-content/uploads/2018/07/2017_ICOM_Statutes_ENpdf",target:"_blank",rel:"noopener noreferrer"},m=a("\u539F\u6587"),f=a("\u6458\u81EAICOM\u3002"),p=c('<h2 id="english" tabindex="-1"><a class="header-anchor" href="#english" aria-hidden="true">#</a> English</h2><p>As amended and adopted by the Extraordinary General Assembly on 9th June 2017 (Paris, France)</p><h3 id="introduction" tabindex="-1"><a class="header-anchor" href="#introduction" aria-hidden="true">#</a> Introduction</h3><p>The Statutes of the International Council of Museums (hereinafter referred to as \u201CICOM\u201D) is the basic document of the Organisation. The Internal Rules of ICOM and the ICOM Code of Ethics for Museums define and complete these Statutes.</p><h3 id="article-1-name-legal-status-location-term-and-fiscal-year" tabindex="-1"><a class="header-anchor" href="#article-1-name-legal-status-location-term-and-fiscal-year" aria-hidden="true">#</a> Article 1. Name, Legal Status, Location, Term and Fiscal Year</h3><h4 id="section-1-name" tabindex="-1"><a class="header-anchor" href="#section-1-name" aria-hidden="true">#</a> Section 1 - Name</h4><p>The name of the Organisation is the \u2018International Council of Museums\u2019 (ICOM). Use of the name and acronym is subject to restrictions and may only be used by and for the uses and benefit of the Organisation and its Members.</p><h4 id="section-2-legal-status" tabindex="-1"><a class="header-anchor" href="#section-2-legal-status" aria-hidden="true">#</a> Section 2 - Legal Status</h4><p>Established in 1946, ICOM is a non-profit organisation subject to French law (the 1901 Associations Act) and a non-governmental organisation that maintains formal relations with the United Nations Educational, Scientific and Cultural Organization (UNESCO) and has a consultative status with the United Nations Economic and Social Council.</p><h4 id="section-3-location" tabindex="-1"><a class="header-anchor" href="#section-3-location" aria-hidden="true">#</a> Section 3 - Location</h4><p>The registered office of ICOM is located at Maison de l\u2019UNESCO, 1 rue Miollis, 75732 Paris Cedex 15, France. Relocation of the registered office within the Paris Region is subject to the approval of the Executive Board. Relocation of the registered office to another part of France or another country is subject to the approval of the General Assembly.</p><h4 id="section-4-term" tabindex="-1"><a class="header-anchor" href="#section-4-term" aria-hidden="true">#</a> Section 4 - Term</h4><p>The term of ICOM shall be indefinite.</p><h4 id="section-5-fiscal-year" tabindex="-1"><a class="header-anchor" href="#section-5-fiscal-year" aria-hidden="true">#</a> Section 5 - Fiscal Year</h4><p>The term of ICOM shall be indefinite.</p><h3 id="article-2-mission-and-purpose" tabindex="-1"><a class="header-anchor" href="#article-2-mission-and-purpose" aria-hidden="true">#</a> Article 2. Mission and Purpose</h3><h4 id="section-1-mission" tabindex="-1"><a class="header-anchor" href="#section-1-mission" aria-hidden="true">#</a> Section 1 - Mission</h4><p>ICOM is the international organisation of museums and museum professionals which is committed to the research, conservation, continuation and communication to society of the world&#39;s natural and cultural heritage, present and future, tangible and intangible.</p><h4 id="section-2-purpose" tabindex="-1"><a class="header-anchor" href="#section-2-purpose" aria-hidden="true">#</a> Section 2 - Purpose</h4><p>ICOM establishes professional and ethical standards for museum activities, makes recommendations on issues related thereto, promotes capacity building, advances knowledge and raises public cultural awareness through global networks and co-operation programmes.</p><h3 id="article-3-definition-of-terms" tabindex="-1"><a class="header-anchor" href="#article-3-definition-of-terms" aria-hidden="true">#</a> Article 3. Definition of Terms</h3><p>Whenever used in these Statutes, the following terms beginning with a capital letter shall bear the meaning defined in the present Article. No distinction shall be made between the singular or plural use thereof.</p><h4 id="secion-1-museum" tabindex="-1"><a class="header-anchor" href="#secion-1-museum" aria-hidden="true">#</a> Secion 1 - Museum</h4><p>A museum is a non-profit, permanent institution in the service of society and its development, open to the public, which acquires, conserves, researches, communicates and exhibits the tangible and intangible heritage of humanity and its environment for the purposes of education, study and enjoyment.</p><h4 id="section-2-\u2013-institutions-recognised-by-icom" tabindex="-1"><a class="header-anchor" href="#section-2-\u2013-institutions-recognised-by-icom" aria-hidden="true">#</a> Section 2 \u2013 Institutions recognised by ICOM</h4><p>After seeking the advice of the Advisory Council, the Executive Board, may recognise other institutions as having some or all of the characteristics of a Museum.</p><h4 id="section-3-\u2013-museum-professionals" tabindex="-1"><a class="header-anchor" href="#section-3-\u2013-museum-professionals" aria-hidden="true">#</a> Section 3 \u2013 Museum Professionals</h4><p>Museum professionals include all staff of the museums and institutions qualifying as museums in accordance with the definition stated within the scope of Article 3, Section 1 and 2 and persons who, in a professional capacity, have as their main activity to provide services, knowledge and expertise for museums and the museum community.</p><h3 id="section-4-\u2013-member-in-good-standing" tabindex="-1"><a class="header-anchor" href="#section-4-\u2013-member-in-good-standing" aria-hidden="true">#</a> Section 4 \u2013 Member in Good Standing</h3><p>An ICOM member in good standing is a person or an institution whose request for membership to ICOM has been accepted under the conditions defined within the scope of Article 4, Section 2 of these Statutes, and has paid the annual membership fee for the respective period and in amount stipulated by the Executive Board.</p><h4 id="section-5-\u2013-state" tabindex="-1"><a class="header-anchor" href="#section-5-\u2013-state" aria-hidden="true">#</a> Section 5 \u2013 State</h4><p>For the purposes of establishing National Committees, a State is defined as a sovereign country that is a member of the United Nations or any of its Specialised Agencies, or is a party to the Statute of the International Court of Justice.</p><h3 id="article-4-membership" tabindex="-1"><a class="header-anchor" href="#article-4-membership" aria-hidden="true">#</a> Article 4. Membership</h3><h3 id="article-5-annual-membership-fee" tabindex="-1"><a class="header-anchor" href="#article-5-annual-membership-fee" aria-hidden="true">#</a> Article 5. Annual Membership Fee</h3><h3 id="article-6-membership-privileges" tabindex="-1"><a class="header-anchor" href="#article-6-membership-privileges" aria-hidden="true">#</a> Article 6. Membership Privileges</h3><h3 id="article-7-voting-rights-at-the-general-assembly-and-the-election-of-the-executive-board" tabindex="-1"><a class="header-anchor" href="#article-7-voting-rights-at-the-general-assembly-and-the-election-of-the-executive-board" aria-hidden="true">#</a> Article 7. Voting Rights at the General Assembly and the Election of the Executive Board</h3><h3 id="article-8-components-of-icom" tabindex="-1"><a class="header-anchor" href="#article-8-components-of-icom" aria-hidden="true">#</a> Article 8. Components of ICOM</h3><h3 id="article-9-governance-structure" tabindex="-1"><a class="header-anchor" href="#article-9-governance-structure" aria-hidden="true">#</a> Article 9. Governance Structure</h3><h3 id="article-10-general-assembly" tabindex="-1"><a class="header-anchor" href="#article-10-general-assembly" aria-hidden="true">#</a> Article 10. General Assembly</h3><h3 id="article-11-executive-board" tabindex="-1"><a class="header-anchor" href="#article-11-executive-board" aria-hidden="true">#</a> Article 11. Executive Board</h3><h3 id="article-12-auditing-of-accounts" tabindex="-1"><a class="header-anchor" href="#article-12-auditing-of-accounts" aria-hidden="true">#</a> Article 12. Auditing of Accounts</h3><h3 id="article-13-advisory-council" tabindex="-1"><a class="header-anchor" href="#article-13-advisory-council" aria-hidden="true">#</a> Article 13. Advisory Council</h3><h3 id="article-14-national-committees" tabindex="-1"><a class="header-anchor" href="#article-14-national-committees" aria-hidden="true">#</a> Article 14. National Committees</h3><h3 id="article-15-national-correspondents" tabindex="-1"><a class="header-anchor" href="#article-15-national-correspondents" aria-hidden="true">#</a> Article 15. National Correspondents</h3><h3 id="article-16-international-committees" tabindex="-1"><a class="header-anchor" href="#article-16-international-committees" aria-hidden="true">#</a> Article 16. International Committees</h3><h3 id="article-17-regional-alliances" tabindex="-1"><a class="header-anchor" href="#article-17-regional-alliances" aria-hidden="true">#</a> Article 17. Regional Alliances</h3><h3 id="article-18-affiliated-organisations" tabindex="-1"><a class="header-anchor" href="#article-18-affiliated-organisations" aria-hidden="true">#</a> Article 18. Affiliated Organisations</h3><h3 id="article-19-general-triennial-conference" tabindex="-1"><a class="header-anchor" href="#article-19-general-triennial-conference" aria-hidden="true">#</a> Article 19. General Triennial Conference</h3><h3 id="article-20-secretariat" tabindex="-1"><a class="header-anchor" href="#article-20-secretariat" aria-hidden="true">#</a> Article 20. Secretariat</h3><h3 id="article-21-income-and-disbursements" tabindex="-1"><a class="header-anchor" href="#article-21-income-and-disbursements" aria-hidden="true">#</a> Article 21. Income and Disbursements</h3><h3 id="article-22-languages" tabindex="-1"><a class="header-anchor" href="#article-22-languages" aria-hidden="true">#</a> Article 22. Languages</h3><h3 id="article-23-validation-and-amendments" tabindex="-1"><a class="header-anchor" href="#article-23-validation-and-amendments" aria-hidden="true">#</a> Article 23. Validation and Amendments</h3><h3 id="article-24-dissolution" tabindex="-1"><a class="header-anchor" href="#article-24-dissolution" aria-hidden="true">#</a> Article 24. Dissolution</h3>',53);function b(g,x){const i=t("ExternalLinkIcon");return n(),r(o,null,[l,e("blockquote",null,[e("p",null,[e("a",u,[m,s(i)]),f])]),p],64)}var y=h(d,[["render",b]]);export{y as default};
